import React, { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { ImodalLogFormError } from "./appFolder-Interfaces";

export default function ModalLogFormError({
  type,
  cantPassBadEmail,
  ErrorDisplay,
  ErrorData,
  darkmode,
  WidthHolder,
}: ImodalLogFormError) {
  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalError = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    transform: ErrorData ? `translateY(0%)` : `translateY(100%)`,
  });

  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalErrorPassword = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    marginTop: "-65px",
    zIndex: 0,
    transform: ErrorData ? `translateY(100%)` : `translateY(0%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#222222";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  return (
    <>
      {type ? (
        <animated.div style={animationModalError}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "-1.5px",
              display: "flex",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    opacity: ErrorDisplay,
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid style={{ opacity: 0.6 }} xs={10}>
                {ErrorData}
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      ) : (
        <animated.div style={animationModalErrorPassword}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",

              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",

              display: "flex",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    opacity: ErrorDisplay,
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid xs={10} style={{ opacity: 0.6 }}>
                {ErrorData}
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      )}
    </>
  );
}
