import React, { useRef, useState, useEffect, useCallback } from "react";
import { Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { useSelector } from "react-redux";
import CircleIcon from "@mui/icons-material/Circle";
import { OptionsSlider } from "./OptionsSlider";

function Menux({ paperPostScrollRef, getSliderWidth }: any) {
  const [shownav, setShownav] = useState<boolean>(true);

  const [shownavTop, setShownavTop] = useState<boolean>(false);

  const [showOptionsmenu, setShowOptionsmenu] = useState<boolean>(false);

  const [startPostScroll, setstartPostScroll] = useState<number>(0);
  const [callstartonce, setcallstartonce] = useState<boolean>(false);
  const [callendonce, setcallendonce] = useState<boolean>(false);
  const [endPostScroll, setendPostScroll] = useState<number>(0);
  const menuTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const menuTimer2 = useRef<ReturnType<typeof setTimeout> | null>(null);
  const menuTimer3 = useRef<ReturnType<typeof setTimeout> | null>(null);
  const menuTimer4 = useRef<ReturnType<typeof setTimeout> | null>(null);

  ///
  ///
  ///
  ///

  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationmenu = useSpring({
    config: {
      duration: 300,
    },
    opacity: shownav ? 1 : 0.5,
    transform: shownav ? `translateY(0%)` : `translateY(-125%)`,
  });

  const jayme = useCallback(() => {
    var datascroll: number = matchPc
      ? paperPostScrollRef.current.scrollTop
      : window.scrollY;

    if (callstartonce) {
      setstartPostScroll(datascroll);
    } else {
    }
    setcallstartonce(true);

    if (menuTimer4.current) {
      if (menuTimer2.current) {
        clearTimeout(menuTimer2.current);
      }
      if (menuTimer3.current) {
        clearTimeout(menuTimer3.current);
      }
      clearTimeout(menuTimer4.current);
    }

    menuTimer4.current = setTimeout(function () {
      if (menuTimer3.current) {
        clearTimeout(menuTimer3.current);
      }
      menuTimer3.current = setTimeout(function () {
        setcallendonce(false);
      }, 150);
      if (menuTimer.current) {
        clearTimeout(menuTimer.current);
      }
      menuTimer.current = setTimeout(function () {
        if (callendonce) {
        } else {
          setendPostScroll(datascroll);
        }
        setcallendonce(true);
        if (menuTimer2.current) {
          clearTimeout(menuTimer2.current);
        }

        menuTimer2.current = setTimeout(function () {
          console.log(endPostScroll);
          console.log(startPostScroll);

          if (endPostScroll > startPostScroll) {
            if (datascroll < 100) {
              setShownav(false);
              setTimeout(function () {
                setShownav(false);
              }, 300);
            } else {
              setShownav(true);
              setTimeout(function () {
                setShownav(true);
              }, 300);
            }
          } else {
            setShownav(false);
            setTimeout(function () {
              setShownav(false);
            }, 300);
          }
          setcallstartonce(false);
        }, 120);
      }, 80);
    }, 300);
  }, [
    window.scrollY,
    shownav,
    menuTimer4.current,
    paperPostScrollRef,
    endPostScroll,
    startPostScroll,
  ]);

  useEffect(() => {
    matchPc
      ? paperPostScrollRef.current.addEventListener("scroll", jayme)
      : window.addEventListener("scroll", jayme);

    return () => {
      matchPc
        ? paperPostScrollRef.current.removeEventListener("scroll", jayme)
        : window.removeEventListener("scroll", jayme);
    };
  }, [jayme]);

  useEffect(() => {
    matchPc
      ? paperPostScrollRef.current.addEventListener("scroll", jayme)
      : window.addEventListener("scroll", jayme);

    return () => {
      matchPc
        ? paperPostScrollRef.current.removeEventListener("scroll", jayme)
        : window.removeEventListener("scroll", jayme);
    };
  }, [jayme]);

  var superFont = "";

  if (matchPc) {
    superFont = "super-starz-text-Pc";

    ///
  } else if (matchTablet) {
    superFont = "super-starz-text-Tablet";

    ///
  } else {
    superFont = "super-starz-text-Mobile";
  }

  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;

  return (
    <>
      (shownavTop{" "}
      <>
        {showOptionsmenu ? (
          <>
            {" "}
            <Grid
              container
              style={{
                top: "0vh",
                position: "fixed",
                width: "100%",
                height: "0px",
                zIndex: 11,
              }}
            >
              <Grid xs={12} item style={{ padding: "0px" }}>
                <Grid
                  item
                  component={Box}
                  display={{ xs: "none", md: "block" }}
                  md={2}
                ></Grid>
                <Grid item xs={12} md={12} style={{ padding: "0px" }}>
                  <OptionsSlider
                    setShowOptionsmenu={setShowOptionsmenu}
                    typeTop={true}
                    getSliderWidth={getSliderWidth}
                  />
                </Grid>
              </Grid>{" "}
            </Grid>
          </>
        ) : (
          <>
            <Grid
              container
              style={{
                top: "0vh",
                position: "fixed",
                width: "100%",
                height: "0px",
                zIndex: 10,
              }}
            >
              {" "}
              <Grid
                item
                component={Box}
                display={{ xs: "none", md: "block" }}
                md={2}
              ></Grid>
              <Grid item xs={12} md={10}>
                <animated.div style={animationmenu}>
                  <span
                    onClick={(e: any) => {
                      setShowOptionsmenu(true);
                    }}
                    className={
                      darkmodeReducer
                        ? `menutopdark ${superFont} turdark zupermenudark`
                        : `menutoplight ${superFont} turlight zupermenulight`
                    }
                    style={{
                      marginLeft: matchPc ? "10px" : "5px",
                      paddingBottom: "7.5px",
                      paddingLeft: "18px",
                      paddingRight: "18px",
                      borderBottomLeftRadius: "15px",
                      borderBottomRightRadius: "15px",
                      verticalAlign: "middle",
                      cursor: "pointer",
                    }}
                  >
                    <span
                      style={{}}
                      className={
                        darkmodeReducer
                          ? "text-superstarz-dark   text-superstarz-dark-colorA  "
                          : "text-superstarz-light  text-superstarz-light-colorA  "
                      }
                    >
                      Super
                    </span>
                    <span
                      style={{
                        opacity: darkmodeReducer ? "0.83" : "0.8",
                      }}
                      className={
                        darkmodeReducer
                          ? "text-superstarz-dark     text-superstarz-dark-colorB  "
                          : "text-superstarz-light   text-superstarz-light-colorB   "
                      }
                    >
                      starZ
                    </span>
                  </span>
                </animated.div>
              </Grid>{" "}
            </Grid>
          </>
        )}
      </>{" "}
      ? : null)
    </>
  );
}

export const Menu = React.memo(Menux);
