import React, { useState, useEffect } from "react";
import { useLocation } from "react-router";
import profilePic from "./images/profile/2602017071517530029820170521192154199Screenshot_2016-05-29-20-10-52_1.jpg";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { matchPc, matchTablet } from "./DetectDevice";
import Axios from "axios";

import SuperstarzIconLight from "./images/ssmall.png";
import SuperstarzIconDark from "./images/sdsmall.png";
import {
  Paper,
  Grid,
  makeStyles,
  createMuiTheme,
  MuiThemeProvider,
} from "@material-ui/core";

interface stateType {
  userdata: { username: string; usercolor: string; userimage: string };
}

function Supercheck() {
  const [darkmode, setDarkmode] = useState<boolean>(false);

  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  const logout = () => {
    Axios.post(`http://${REACT_APP_SUPERSTARZ_URL}/logout`, {
      withCredentials: true,
    })
      .then((response) => {
        if (response.data.message === "cookie deleted") {
          alert("logout  complete");
        } else if (response.data.message === "cookie null") {
          alert("logged out  already");
        }
      })
      .catch(function (error) {
        alert("Connection failure ");
      });
  };

  //
  //
  //
  //SETS DARKMODE FROM LOCAL STORAGE IF NOT EMPTY
  useEffect(() => {
    let themelocaldata = JSON.parse(localStorage.getItem("darkmode")!);
    if (themelocaldata !== null) {
      setDarkmode(themelocaldata);
    }
  }, []);

  interface IsuperCheckVariables {
    logoimage: string;
    secondarymaincolor: string;
    maincolor: string;
    moreoptions: string;
  }

  var superCheckVariables: IsuperCheckVariables = {
    logoimage: "",
    secondarymaincolor: "",
    maincolor: "",
    moreoptions: "",
  };

  var superCheckVariablesDARK: IsuperCheckVariables = {
    logoimage: SuperstarzIconDark,
    secondarymaincolor: "#ccccccff",
    maincolor: "#ccccccff",
    moreoptions: "supercheck-options-dark",
  };

  var superCheckVariablesLIGHT: IsuperCheckVariables = {
    logoimage: SuperstarzIconLight,
    secondarymaincolor: "#0b111b",
    maincolor: "#0b111b",
    moreoptions: "supercheck-options-light",
  };

  if (darkmode) {
    superCheckVariables = superCheckVariablesDARK;
  } else {
    superCheckVariables = superCheckVariablesLIGHT;
  }

  let themeGeneralSettings = createMuiTheme({
    palette: {
      primary: {
        main: `${superCheckVariables.maincolor}`,
      },
      secondary: {
        main: `${superCheckVariables.secondarymaincolor}`,
      },
      type: darkmode ? "dark" : "light",
    },
  });

  var useStyle = makeStyles((themeGeneralSettings) => ({
    optionsContainer: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "3vh",
        top: "1em",
        fontWeight: "bold",
        display: "flex",
        alignItems: "center",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        padding: "4.5vh",
        top: "1em",
        fontWeight: "bold",
        display: "flex",
        alignItems: "center",
      },
    },
    PaperStyle: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        backgroundImage: darkmode
          ? "linear-gradient(0deg, #282c34, #282c36, #191919 )"
          : "linear-gradient(0deg, rgb(220,225,225), rgb(235,237,239),rgb(246,250,255) )",
      },
    },
  }));

  var classes = useStyle();

  var imageWidth = "";
  var optionsClass = "";
  var iconSize = "";
  var fontOptions = "";
  var pad = "";
  var padR = "";
  var margintopp = "";
  var justifyContentt = "";

  if (matchPc) {
    imageWidth = "30%";
    iconSize = "5%";
    optionsClass = "supercheck-optionsPc";
    fontOptions = "3.8rem";
    pad = "33px";
    padR = "20px";
    margintopp = "1.5vh";
    justifyContentt = "flex-start";
  } else if (matchTablet) {
    imageWidth = "50%";
    iconSize = "11%";
    optionsClass = "supercheck-optionsTablet";
    fontOptions = "4rem";
    pad = "33px";
    padR = "20px";
    margintopp = "6vh";
    justifyContentt = "flex-start";
  } else {
    imageWidth = "66%";
    iconSize = "22%";
    optionsClass = "supercheck-optionsMobile";
    fontOptions = "2rem";
    pad = "10px";
    padR = "5px";
    margintopp = "4vh";
    justifyContentt = "flex-start";
  }

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper className={classes.PaperStyle} style={{ borderRadius: "0px" }}>
        <Grid
          container
          style={{
            padding: pad,
            paddingRight: padR,
            position: "absolute",
            top: "0em",
            fontFamily: "kaushan_scriptregular",
            transition: "opacity 1.5s",
            display: "flex",
          }}
        >
          <Grid
            xs={12}
            item
            style={{
              fontFamily: "kaushan_scriptregular",
              fontSize: "2.41vw",
              fontWeight: "bolder",
              display: "flex",
              alignItems: "center",
              justifyContent: justifyContentt,
              marginTop: margintopp,
            }}
          >
            <img
              className="buttonshake"
              onClick={logout}
              style={{
                objectFit: "contain",
                width: iconSize,
                opacity: darkmode ? "0.3" : "0.42",
                zIndex: 21,
              }}
              src={superCheckVariables.logoimage}
              alt="SuperstarZ logo"
            />
          </Grid>
        </Grid>

        <Grid container xs={12} style={{ height: "100vh", width: "100%" }}>
          <Grid item xs={12}>
            <Grid
              item
              xs={12}
              className="center-content-vertically dontallowhighlighting"
              style={{
                cursor: "pointer",
                zIndex: 0,
                margin: "auto",
                display: "grid",
                position: "relative",
                marginTop: "-16px",
              }}
            >
              <Grid
                className={`${superCheckVariables.moreoptions}   ${optionsClass}  buttonshakelogoption `}
                style={{ zIndex: 1 }}
              >
                <MoreHorizIcon
                  className="make-plainIcons-clickable"
                  style={{ fontSize: fontOptions }}
                />
              </Grid>
              <img
                className="make-small-image-clickable-neutral"
                style={{
                  position: "relative",
                  objectFit: "contain",
                  width: imageWidth,
                  borderRadius: "200px",
                  margin: "auto",
                }}
                src={profilePic}
                alt="SuperstarZ logo"
              />
            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export default Supercheck;
