import React, { useRef, useState } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";

import { useSpring, animated } from "react-spring";

function OptionsSliderx({ getSliderWidth }: any) {
  const optionsImages = ["1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.jpg"];

  const [translate, setTranslate] = useState(0);

  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 300,
    },
    transform: `translateX(-${translate}px)`,
    transition: "0.45s",
    height: "auto",
    display: "flex",
    width: `${getSliderWidth}px`,
    backgroundColor: "#00ccff",
  });

  return (
    <>
      <animated.div style={modalanimation}>
        {optionsImages.map((im: any, i: any) => (
          <img
            className="turprofileLight"
            style={{
              width: "100%",
              height: "100%",
              borderRadius: "50%",
              padding: "3px",
              objectFit: "cover",
              marginLeft: "13px",
              marginTop: "15px",
              boxShadow: "0 0 3.8px blue",
              marginBottom: "2px",
            }}
            src={`./images/options/${im}`}
          />
        ))}
      </animated.div>
    </>
  );
}

export const OptionsSlider = React.memo(OptionsSliderx);
