import React, { useState, useCallback } from "react";

import {
  IconButton,
  InputAdornment,
  TextField,
  DialogContent,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";
import { ItextfIeldLogin } from "./log-Interfaces";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import { useSelector } from "react-redux";
import CircularProgress from "@material-ui/core/CircularProgress";

function PasswordCheckx({
  widthHolder,
  checkSignupPassword,
  setcheckSignupPassword,
  checkSignupPasswordACTIVATE,
  setcheckSignupPasswordACTIVATE,
}: any): JSX.Element {
  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const [showSpinner, setshowSpinner] = useState<boolean>(true);

  /////
  var sizex: "small" | "medium" | undefined = undefined;
  var font1 = " ";
  var font2 = " ";
  var transform = "";
  var widthcheckPassword = "";
  var heighttt = " ";
  var positionn: any = "";
  var bottomm = "";
  var displayy = "";

  ///////

  if (matchPc) {
    var heighttt = "100vh";
    sizex = "medium";
    font1 = "2.7vh";
    font2 = "1.9vh";
    transform = "scale(1)";
    widthcheckPassword = widthHolder;
    positionn = "";
    bottomm = "";
    displayy = "none";
  } else if (matchTablet) {
    var heighttt = "95.2vh";
    sizex = "small";
    font1 = "2.6vh";
    font2 = "2vh";
    transform = "scale(1)";
    widthcheckPassword = "62%";
    positionn = "fixed";
    bottomm = "0em";
    displayy = "block";
  } else {
    var heighttt = "95.2vh";
    sizex = "small";
    font1 = "";
    font2 = "";
    transform = "scale(0.93)";
    widthcheckPassword = "100%";
    positionn = "fixed";
    bottomm = "0em";
    displayy = "block";
  }

  ///
  ///
  ///
  /// SANITISE LOG IN FORM INPUT CLIENT SIDE
  const updatecheckSignupPasswordvalues = useCallback(
    (e: any) => {
      const { value } = e.target;
      setcheckSignupPassword(value);
    },

    [checkSignupPassword]
  );

  ///
  ///
  ///
  ///MATERIAL UI SPINNER STYLES (INSPIRED BY FACEBOOK SPINNERS)
  const useStylesmodalformsignup = makeStyles((theme) => ({
    root: {
      position: "relative",
      textAlign: "left",
      left: 10,
    },
    bottom: {
      color: theme.palette.grey[theme.palette.type === "light" ? 300 : 700],
    },
    top: {
      color: "#00ccff",
      animationDuration: "550ms",
      position: "absolute",
      left: 0,
    },
    circle: {
      strokeLinecap: "round",
    },
  }));

  const classes = useStylesmodalformsignup();

  return (
    <>
      {checkSignupPasswordACTIVATE ? (
        <>
          <DialogContent
            className={
              darkmodeReducer
                ? " dontallowhighlighting  mobileTextfield-backplateColorDark"
                : " dontallowhighlighting  mobileTextfield-backplateColorLight"
            }
            style={{
              zIndex: 12,
              display: displayy,
              bottom: "0em",
              height: "95.5vh",
              overflow: "hidden",
              width: "100%",
              position: "fixed",
              marginTop: "-3px",
            }}
          ></DialogContent>
          <div
            className={classes.root}
            style={{ opacity: "1", zIndex: 14, position: "fixed", top: "14em" }}
          >
            <CircularProgress
              variant="determinate"
              className={classes.bottom}
              size={100}
              thickness={4}
              value={100}
            />
            <CircularProgress
              variant="indeterminate"
              disableShrink
              className={classes.top}
              classes={{
                circle: classes.circle,
              }}
              size={100}
              thickness={4}
            />
          </div>{" "}
          <TextField
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            style={{
              transform: transform,
              width: widthcheckPassword,
              position: "relative",
              top: "-22vh",
              zIndex: 14,
            }}
            label="Confirm Password"
            type="text"
            onChange={updatecheckSignupPasswordvalues}
            name="inputedUsername"
            value={checkSignupPassword}
            variant="standard"
          />
        </>
      ) : null}
    </>
  );
}

export const PasswordCheck = React.memo(PasswordCheckx);
