import "./App.css";
import "typeface-roboto";
import React, { useState } from "react";
import imyz from "./images/s.png";
import imyzd from "./images/sd.png";
import { PcLogin, MobileLogin } from "./app-folder/Login";
import {
  useTheme,
  Paper,
  useMediaQuery,
  Grid,
  makeStyles,
  createMuiTheme,
  Typography,
  MuiThemeProvider,
} from "@material-ui/core";
import Option from "./app-folder/Option";
import * as CSS from "csstype";

function App(): JSX.Element {
  var usetheme = useTheme();
  var match = useMediaQuery(usetheme.breakpoints.up("sm"));
  const [darkmode, setDarkmode] = useState<boolean>(true);

  interface IappVariables {
    shade: string;
    shade2: string;
    shade2num: string;
    shade2nump: string;
    logoimage: string;
    secondarymaincolor: string;
    maincolor: string;
    shade2nump22: string;
  }

  var appVariables: IappVariables = {
    shade: "",
    shade2: "",
    shade2num: "",
    shade2nump: "",
    logoimage: "",
    secondarymaincolor: "",
    maincolor: "",
    shade2nump22: "",
  };

  var appVariablesDARK: IappVariables = {
    shade: "#cccccc",
    shade2: "#cccccc",
    shade2num: "1.1",
    shade2nump: "1.8",
    logoimage: imyzd,
    secondarymaincolor: "#ffffff",
    maincolor: "#ffffff",
    shade2nump22: "5.5",
  };

  var appVariablesLIGHT: IappVariables = {
    shade: "#0b1728",
    shade2: "#0b1728",
    shade2num: "1.5",
    shade2nump: "1.5",
    logoimage: imyz,
    secondarymaincolor: "#0b1728",
    maincolor: "#0b1728",
    shade2nump22: "8",
  };

  if (darkmode) {
    appVariables = appVariablesDARK;
  } else {
    appVariables = appVariablesLIGHT;
  }

  let themeGeneralSettings = createMuiTheme({
    palette: {
      primary: {
        main: `${appVariables.maincolor}`,
      },
      secondary: {
        main: `${appVariables.secondarymaincolor}`,
      },
      type: darkmode ? "dark" : "light",
    },
  });

  var useStyle = makeStyles((themeGeneralSettings) => ({
    paperstyle: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        height: "100vh",
        textAlign: "center",
        backgroundImage: darkmode
          ? "linear-gradient(0deg, #282c34, #282c36, #191919 )"
          : "linear-gradient(0deg, rgb(222,225,225), rgb(238,237,239),rgb(250,250,255) )",
      },
    },

    icon: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        objectFit: "contain",
        width: "78%",
        paddingLeft: "1.5vw",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        objectFit: "contain",
        width: "75%",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        objectFit: "contain",
        width: "56%",
        transition: " 0.5s transform ease-in",
        cursor: "pointer",
      },
      "&:hover": {
        transition: "560ms transform ease-in",
        transform: "scale(1.06)",
      },
      "&:active": {
        transition: "200ms transform ease-in",
        transform: "scale(0.94)",
      },
    },

    h1: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        fontFamily: "BROADW",
        fontSize: "9.8vw",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        fontFamily: "BROADW",
        fontSize: "8vw",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        fontFamily: "BROADW",
        fontSize: "5.8vw",
      },
    },

    loginclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.5px",
        borderRadius: "20px",
        fontSize: "13px",
        mozBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        webkitBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        boxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
      },
    },

    signupclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "10.5px",
        borderRadius: "20px",
        fontSize: "13px",
        mozBoxShadow: `0 0 4.5px ${appVariables.shade}`,
        webkitBoxShadow: `0 0 4.5px ${appVariables.shade} `,
        boxShadow: `0 0 4.5px ${appVariables.shade}`,
      },
    },
    loginclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        boxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
      },
    },
    signupclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        boxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
      },
    },
  }));
  var classes = useStyle();

  const fontDataPc: CSS.Properties = { fontSize: "2.2rem" };

  const fontDataMobile: CSS.Properties = {
    fontSize: "1.5rem",
  };

  const marginDataPc: CSS.Properties = {
    marginLeft: "-20px",
  };

  const marginDataMobile: CSS.Properties = {
    marginLeft: "-5.5px",
  };

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper className={classes.paperstyle} style={{ borderRadius: "0px" }}>
        {match ? (
          <Option
            switchsize="medium"
            darkmode={darkmode}
            setDarkmode={setDarkmode}
            marginData={marginDataPc}
            fontData={fontDataPc}
          />
        ) : (
          <Option
            switchsize="small"
            darkmode={darkmode}
            setDarkmode={setDarkmode}
            marginData={marginDataMobile}
            fontData={fontDataMobile}
          />
        )}

        <Grid
          container
          className={
            match ? "containerapp fadeboyin " : "containerappmobile fadeboyin "
          }
        >
          <Grid xs={4} sm={3} md={4}></Grid>
          <Grid item className="centericon   dontallowselect" xs={4} md={4}>
            <img
              className={classes.icon}
              src={appVariables.logoimage}
              alt="SuperstarZ logo"
            />
          </Grid>
          <Grid xs={4} md={4}></Grid>
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export default App;
