import React, {
  useRef,
  useEffect,
  useCallback,
  useState,
  useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import DoneIcon from '@mui/icons-material/Done';
import CropIcon from '@mui/icons-material/Crop';

function Taskbarx({
  showModalUploadTask,
  uploadClose,

}: any): JSX.Element {



  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;


  const animation = useSpring({
    config: {
      duration: 600,
    },
    opacity: showModalUploadTask ? 1 : 0,
    transform: showModalUploadTask ? `translateX(0%)` : `translateX(-100%)`,
    padding: "0px",

  });




  const cropCanvasRef: any = useRef(null);

  const navRef: any = useRef(null);

  const IconRef: any = useRef(null);

  const IconRef2: any = useRef(null);

  const [allowCropAllCanvas, setallowCropAllCanvas] = useState<boolean>(true);

  const [CropImageHolder, setCropImageHolder] = useState<any>(null);

  const [imageHeight, setimageHeight] = useState(0);
  const [imageWidth, setimageWidth] = useState(0);

  const [Box, setBox] = useState<boolean>(false);



  const { activatefilterImage, activatecropImage, selectedImage, cropimage } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.GlobalNavuploadReducer,
    })
  );
  const activatefilterImageReducer = activatefilterImage;
  const activatecropImageReducer = activatecropImage;
  const selectedImageReducer = selectedImage;
  const cropimageReducer = cropimage;





  function resizeCanvas() {
    const imgAspectRatio = cropCanvasRef.current.width / cropCanvasRef.current.height;
    var screenW = navRef.current.clientWidth;
    var screenH = navRef.current.clientHeight;
    let canvasW = screenW
    let canvasH = screenH
    if (imgAspectRatio <= 1.55) {
      canvasH = screenH;
      canvasW = canvasH * imgAspectRatio;
    } else {
      canvasW = screenW;
      canvasH = canvasW / imgAspectRatio;
    }
    cropCanvasRef.current.style.height = `${canvasH}px`;
    cropCanvasRef.current.style.width = `${canvasW}px`;

    IconRef.current.style.top = `${canvasH / 1.18}px`;
    IconRef2.current.style.top = `${canvasH / 1.18}px`;


  }




  function GiveImageSpecificDimensions(Newcropimage: any) {
    const imgAspectRatio = Newcropimage.naturalWidth / Newcropimage.naturalHeight;
    var canvasW;
    var canvasH;
    if (imgAspectRatio <= 1.55) {
      var screenH = 1920;
      canvasH = screenH
      canvasH = screenH;
      canvasW = canvasH * imgAspectRatio;
    } else {
      var screenW = 2048;
      canvasW = screenW
      canvasW = screenW;
      canvasH = canvasW / imgAspectRatio;
    }
    setimageHeight(canvasH);
    setimageWidth(canvasW);
  }


  useLayoutEffect(() => {
    const Newcropimage: any = new Image();
    Newcropimage.src = cropimageReducer;
    Newcropimage.onload = function () {
      if (CropImageHolder === Newcropimage) {
      } else {
        setCropImageHolder(Newcropimage);
        GiveImageSpecificDimensions(Newcropimage);
      }
    }
  }, [cropimageReducer]);



  useLayoutEffect(() => {
    if (CropImageHolder && cropCanvasRef.current) {
      const ctx = cropCanvasRef.current.getContext("2d");
      const imgAspectRatio = imageWidth / imageHeight;
      if (Box) {
        if (imgAspectRatio <= 1.3) {
          cropCanvasRef.current.width = imageWidth;
          cropCanvasRef.current.height = imageWidth;
        } else {
          cropCanvasRef.current.width = imageHeight;
          cropCanvasRef.current.height = imageHeight;
        }
      } else {
        cropCanvasRef.current.width = imageWidth;
        cropCanvasRef.current.height = imageHeight;
      }
      resizeCanvas();
      const w = imageWidth;
      const h = imageHeight;
      ctx.drawImage(CropImageHolder, 0, 0, w, h);
    }
  }, [CropImageHolder, imageWidth, imageHeight])


  return (
    <>
      <animated.div ref={navRef} style={{
        ...animation, width: '94%',
        height: "100vh",
        position: "fixed",
        top: '-0vh',
        zIndex: 100,
        cursor: "default",
        overflow: "hidden",
        backgroundColor: '#00ccff'
      }}>
        <DialogContent
          style={{
            padding: "0px",
          }}>



          <Grid
            container
            style={{
              padding: "0px", width: '100%'
            }}>

            {allowCropAllCanvas ? (
              <canvas
                ref={cropCanvasRef}
                style={{
                  padding: "0px",
                  margin: "auto",
                  backgroundColor: '#000000',
                  cursor: "pointer",
                  overflow: 'hidden'
                }} />
            ) : null}



            <DoneIcon
              ref={IconRef}
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
              }
              style={{
                color: "#ffffff",
                fontSize: "3.1vw",
                position: "absolute",
                opacity: 0.76,
                left: '43%',
                right: "",
              }}
            />



            <CropIcon
              ref={IconRef2}
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
              }
              style={{
                color: "#ffffff",
                fontSize: "2.9vw",
                position: "absolute",
                bottom: '5vh',
                opacity: 0.8,
                right: '43%',

              }}
            />

          </Grid>

        </DialogContent> </animated.div>


    </>
  );
}

export const Taskbar = React.memo(Taskbarx);
