import { animated, useSpring } from "react-spring";
import { Grid, DialogContent } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { IServerError } from "./appFolder-Interfaces";
import "./ServerError.css";
export default function ServerError({
  device,
  serverEmojiplain,
  setServerErrorData,
  serverErrorDisplay,
  serverErrorData,
  darkmode,
}: IServerError) {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING SERVER ERROR
  const serverErrorAnimation = useSpring({
    config: {
      duration: 500,
    },
    opacity: serverErrorDisplay,
    transform: serverErrorData ? `translateY(0%)` : `translateY(-100%)`,
  });

  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";
  var Backtype = "";
  var textback = "";
  var ErrorOutlineIconTop = "";
  var ErrorOutlineIconLeft = "";
  var ErrorOutlineIconFont = "";
  var severerrorData = "";
  var severerrorEmoji = "";
  var severerrorEmojiLeft = "";

  if (device === "pc") {
    ErrorOutlineIconTop = "5px";
    ErrorOutlineIconLeft = "";
    ErrorOutlineIconFont = "2.8rem";
    severerrorData = "1.15vw";
    severerrorEmoji = "1.6vw";
    severerrorEmojiLeft = "";
  } else if (device === "mobile") {
    ErrorOutlineIconTop = "0px";
    ErrorOutlineIconLeft = "0.7vw";
    ErrorOutlineIconFont = "1.8rem";
    severerrorData = "2.2vh";
    severerrorEmoji = "2.9vh";
    severerrorEmojiLeft = "2vw";
  } else {
  }

  if (darkmode) {
    ErrorColor = "rgb(34, 34, 34, 0.05)";
    ErrorTextColor = "#ffffff";
    ErrorIconColor = "red";
    textback = "capFonttion-dark";
  } else {
    ErrorColor = "rgb(238, 238, 238 0.09)";
    ErrorTextColor = "#000000";
    ErrorIconColor = "blue";
    textback = "caption-light";
  }

  darkmode
    ? (Backtype = "error-background-dark")
    : (Backtype = "error-background-light");

  return (
    <>
      <DialogContent
        onClick={() => setServerErrorData(null)}
        className={
          serverErrorData
            ? ` dontallowhighlighting ${Backtype} server-error`
            : `display-off`
        }
      >
        <animated.div style={serverErrorAnimation}>
          <Grid
            container
            className="server-error-container"
            style={{
              backgroundColor: ErrorColor,
            }}
          >
            <Grid
              item
              xs={12}
              className="server-error-inner"
              style={{
                color: ErrorTextColor,
              }}
            >
              <Grid item xs={1} sm={2}>
                <ErrorOutlineIcon
                  className="element-bop2 server-error-icon"
                  style={{
                    marginLeft: ErrorOutlineIconLeft,
                    marginTop: ErrorOutlineIconTop,
                    fontSize: ErrorOutlineIconFont,
                    color: ErrorIconColor,
                    WebkitFilter: `drop-shadow(1.9px 0.1px 0.2px ${ErrorIconColor})`,
                    filter: `drop-shadow(1.9px 0.1px 0.2px ${ErrorIconColor})`,
                  }}
                />
              </Grid>
              <Grid
                item
                className="sever-error-data"
                style={{ fontSize: severerrorData, paddingLeft: "15px" }}
                xs={10}
                sm={9}
              >
                <span className={textback}>
                  {serverErrorData}, please try again.
                </span>
              </Grid>
              <Grid
                item
                className={
                  serverEmojiplain
                    ? "display-on server-error-emoji"
                    : "display-off"
                }
                xs={1}
              >
                <span
                  className="server-error-emoji-span"
                  style={{
                    fontSize: severerrorEmoji,
                    marginLeft: severerrorEmojiLeft,
                  }}
                >
                  &#128517;
                </span>
              </Grid>
              <Grid
                item
                className={
                  serverEmojiplain
                    ? "display-off"
                    : "display-on server-error-emoji"
                }
                xs={1}
              >
                <span
                  className="server-error-emoji-span"
                  style={{
                    fontSize: severerrorEmoji,
                    marginLeft: severerrorEmojiLeft,
                  }}
                >
                  &#128543;
                </span>
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      </DialogContent>
    </>
  );
}
