import React, { useState, useRef, useCallback } from "react";
import { Scrollbars } from "react-custom-scrollbars";
import { matchPc } from "../DetectDevice";
import { Profile } from "./Profile";
import { RootStateOrAny, useSelector } from "react-redux";
import { Paper, Grid, Box } from "@material-ui/core";
function ProfileOutter() {
  ///
  ///
  ///TYPES FOR ISLOGGEDIN
  interface RootStateIsLogged {
    IsLoggedReducer: {
      loggedIn: boolean;
    };
  }
  const [paperHeight, setPaperHeight] = useState("auto");

  ///
  ///LOGGED IN DATA FROM REDUX
  const { loggedIn } = useSelector((state: RootStateIsLogged) => ({
    ...state.IsLoggedReducer,
  }));
  const loggedInReducer = loggedIn;

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );
  var PaperStyleReducer = "";

  if (darkmodeReducer) {
    PaperStyleReducer = PaperStyleDark;
  } else {
    PaperStyleReducer = PaperStyleLight;
  }

  const paperscrollRef = useRef<any>(null);

  const autoScrollWindowNImage: any = useCallback(
    (limiter: number) => {
      if (limiter === 1) {
        imagescrollRef.current.scrollTo(0, 0);
        if (formtype) {
          contentScrollRef.current.scrollTo(0, 45);
        } else {
          contentScrollRef.current.scrollTo(0, 20);
        }
      } else {
        setTimeout(function () {
          if (imagescrollRef.current && contentScrollRef.current) {
            imagescrollRef.current.scrollTo(0, 0);
            contentScrollRef.current.scrollTo(0, 30);
          }
        }, 970);
      }
    },
    [formtype]
  );

  return loggedInReducer ? (
    <>
      <Paper
        style={{
          backgroundImage: PaperStyleReducer,
          borderRadius: "0px",
        }}
      >
        {matchPc ? (
          <Scrollbars
            autoHide={true}
            autoHideDuration={1000}
            autoHideTimeout={6000}
            style={{ height: "100vh" }}
            renderTrackHorizontal={(props) => (
              <div {...props} className="track-horizontal" />
            )}
            renderThumbHorizontal={(props) => (
              <div {...props} className="thumb-horizontal" />
            )}
            renderTrackVertical={(props) => (
              <div {...props} className="track-vertical" />
            )}
            renderThumbVertical={(style, props) => (
              <div
                {...props}
                style={{ ...style, width: "60px" }}
                className={
                  darkmodeReducer ? "thumb-verticalDARK" : "thumb-verticalLIGHT"
                }
              />
            )}
            renderView={(props) => <div {...props} className="view" />}
          >
            <Profile paperHeight={paperHeight} />
          </Scrollbars>
        ) : (
          <Profile paperHeight={paperHeight} />
        )}
      </Paper>
    </>
  ) : null;
}

export default ProfileOutter;
