import React, {
  useRef,
  useState,
  useEffect,
  useLayoutEffect,
  useCallback,
} from "react";

import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import Cropper from "react-easy-crop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import CloseIcon from "@mui/icons-material/Close";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Slider from "@mui/material/Slider";
import { AnySoaRecord } from "dns";
import { PreviewCanvas } from "./PreviewCanvas";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";

interface HTMLVideoElementWithCapture extends HTMLVideoElement {
  captureStream(): MediaStream;
}



function VideoEditorx({ VideoUrl, VideoUrl2, ShowVideo2, ShowVideo, setShowVideo2, setShowVideo }: any): JSX.Element {

  ///

  const videoPlayerRef = useRef<HTMLVideoElementWithCapture>(null);

  const Timer2 = useRef<ReturnType<typeof setTimeout> | null>(null);
  const Timer1 = useRef<ReturnType<typeof setTimeout> | null>(null);

  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const [currentTimestamp, setCurrentTimestamp] = useState(0);

  const updateCurrentTimestamp = () => {
    if (videoPlayerRef.current) {
      var x = videoPlayerRef.current.currentTime;
      setCurrentTimestamp(x);

      setprocessing(true);

      startRecording(VideoUrl, x - 1, 17);
    }
  };

  // New state for recorded video URL
  const [recordedVideoUrl, setRecordedVideoUrl] = useState(null);

  const [rec, setRec] = useState(false);

  const [processing, setprocessing] = useState(false);

  const recorderRef: any = useRef(null);


  const [Preview, setPreview] = useState(false);


  const stopRecording = () => {
    if (recorderRef.current && recorderRef.current.state === "recording") {
      recorderRef.current.stop();
      if (videoPlayerRef.current) {
        videoPlayerRef.current.pause();
      }

      if (Timer2.current) {
        clearTimeout(Timer2.current);
      }

      if (Timer1.current) {
        clearTimeout(Timer1.current);
      }
      setprocessing(false);
    }
  };


  const startRecording = (sourceVideoUrl: any, startTime: any, duration: any) => {
    setRec(false);
    const video = videoPlayerRef.current;

    if (video) {
      video.src = sourceVideoUrl;
      video.currentTime = startTime;

      video.onloadedmetadata = () => {
        video.currentTime = startTime;
      };

      video.onseeked = () => {
        video.play();
        const stream = video.captureStream();

        // Specify MIME type and quality parameters
        const options = {
          mimeType: 'video/webm; codecs=vp9', // or 'video/mp4' based on browser support
          videoBitsPerSecond: 750000 // Adjust bitrate as needed
        };

        if (MediaRecorder.isTypeSupported(options.mimeType)) {
          recorderRef.current = new MediaRecorder(stream, options);
        } else {
          recorderRef.current = new MediaRecorder(stream); // Fallback to default settings
        }

        const chunks: any = [];
        recorderRef.current.ondataavailable = (e: any) => chunks.push(e.data);
        recorderRef.current.onstop = () => {
          const blob = new Blob(chunks, { type: options.mimeType });
          const url: any = URL.createObjectURL(blob);
          setRecordedVideoUrl(url);
          video.pause();

          if (Timer1.current) {
            clearTimeout(Timer1.current);
          }
          if (Timer2.current) {
            clearTimeout(Timer2.current);
          }
          Timer1.current = setTimeout(() => {
            setRec(true);
            setprocessing(false);
          }, 2000);
        };

        recorderRef.current.start();
        if (Timer2.current) {
          clearTimeout(Timer2.current);
        }

        if (Timer1.current) {
          clearTimeout(Timer1.current);
        }
        Timer2.current = setTimeout(() => {
          recorderRef.current && recorderRef.current.stop();
        }, duration * 1000);
      };

      video.load();
    }
  };

  // Call this function when you want to start recording
  // For example: startRecording(VideoUrl, 0, 10) for a 10-second clip from the start

  return (
    <>

      {Preview ?
        <Grid xs={12} style={{
          display: ShowVideo || ShowVideo2 ? 'block' : 'none', position: 'fixed', top: '0vh', height: '100vh',
          backgroundColor: '#555555', width: '100%', margin: 'auto', textAlign: 'center'
        }} >
          {recordedVideoUrl && rec ? <video src={recordedVideoUrl} controls style={{
            width: '50%', margin: 'auto', textAlign: 'center'

          }}></video> : null}
          <Grid item xs={12} style={{
            bottom: '-7vh', position: 'relative', display: 'flex', alignItems: 'center',
            justifyContent: 'center', fontSize: '24px', fontFamily: 'Helvetica, Arial, sans-serif', color: '#ffffff'
          }}>
            <Grid item xs={1}

              onClick={() => {
                if (processing) { } else {
                  updateCurrentTimestamp();
                }

              }}
              style={{
                cursor: 'pointer',
                height: '15vh', border: '2px solid white', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center',

              }}>
              {processing ? currentTimestamp : 'START'}
            </Grid>


            <Grid item onClick={() => {
              stopRecording();
            }} xs={3} style={{
              cursor: 'pointer',
              height: '15vh', border: '2px solid white', borderRadius: '8px',
              display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px',
            }}>
              END
            </Grid>
          </Grid>
        </Grid >


        : <Grid xs={12} style={{
          display: ShowVideo || ShowVideo2 ? 'block' : 'none', position: 'fixed', top: '0vh', height: '100vh',
          backgroundColor: '#555555', width: '100%', margin: 'auto', textAlign: 'center'
        }} >
          <video ref={videoPlayerRef} src={VideoUrl} controls style={{
            width: '50%', margin: 'auto', textAlign: 'center'

          }}></video>
          <Grid item xs={12} style={{
            bottom: '-7vh', position: 'relative', display: 'flex', alignItems: 'center',
            justifyContent: 'center', fontSize: '24px', fontFamily: 'Helvetica, Arial, sans-serif', color: '#ffffff'
          }}>
            <Grid item xs={2}

              onClick={() => {
                if (processing) { } else {
                  updateCurrentTimestamp();
                }

              }}
              style={{
                cursor: processing ? 'default' : 'pointer',
                opacity: processing ? 0.25 : 1,
                height: '15vh', border: '2px solid white', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center',

              }}>
              {processing ? currentTimestamp : 'START'}
            </Grid>


            {processing ? <Grid className="blinking" item xs={2} style={{
              height: '15vh', border: '0px solid white', borderRadius: '8px',
              display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px', visibility: 'visible'
            }}>
              processing ¬ <span style={{ fontSize: '1.9vw' }}>&#128119;</span>
            </Grid> :
              <Grid item xs={2}

                onClick={() => {
                  setPreview(true);
                }} style={{
                  height: '15vh', border: '2px solid white', borderRadius: '8px',
                  display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px',
                  visibility: recordedVideoUrl && rec ? 'visible' : 'visible'
                }}>
                PREVIEW
              </Grid>}
            <Grid item onClick={() => {
              stopRecording();
            }} xs={2} style={{
              cursor: processing ? 'pointer' : 'default',
              opacity: processing ? 1 : 0.25,
              height: '15vh', border: '2px solid white', borderRadius: '8px',
              display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px',
            }}>
              END
            </Grid>
          </Grid>
        </Grid >


      }







      <Grid
        item
        xs={12}
        style={{
          margin: "auto",
          textAlign: "right",
          left: '5px',
          height: "20px",
          position: 'fixed',
          top: '3vh',
          right: '3vw'
        }}
      >
        <CloseIcon
          onClick={() => {
            setShowVideo(false);
            setShowVideo2(false);

          }}
          className={
            darkmodeReducer
              ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
              : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
          }
          style={{
            margin: "auto",
            color: "#ffffff",
            fontSize: matchMobile
              ? `3vh`
              : `2.5vw`,

          }}
        />
      </Grid>



      {/* Preview for recorded video */}
      {
        recordedVideoUrl && rec ? <video src={recordedVideoUrl} controls style={{
          width: '40%', margin: 'auto',
          textAlign: 'center', visibility: 'hidden',
        }}></video> : null
      }


    </>
  );
}

export const VideoEditor = React.memo(VideoEditorx)
