import React, { useRef, useState, useEffect, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { useSelector } from "react-redux";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import Crop54Icon from "@mui/icons-material/Crop54";
import CropPortraitIcon from "@mui/icons-material/CropPortrait";
import CommentIcon from "@mui/icons-material/Comment";

function Postx({
  pey,
  refy,
  onPostsItemload,
  post,
  length,
  itemheight,
  itemheighthold,
  postbackheight,
  itemcroptype,
}: any) {
  var paddingTopemo = "4vh";
  var fontSizeemo = "2.7%";
  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  const [itemInfoTopShow, setitemInfoTopShow] = useState<boolean>(false);

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    setitemInfoTopShow(false);
    setTimeout(function () {
      setitemInfoTopShow(true);
    }, 500);
  }, [itemheighthold, itemheight]);

  var textback = "";
  if (darkmodeReducer) {
    textback = "caption-darkPost";
  } else {
    textback = "caption-lightPost";
  }

  return (
    <>
      <div style={{ scrollSnapAlign: "start", width: "100%", padding: "0px" }}>
        {/*///////////////////////////////////////////////////////////////////////////POST DATA*/}
        <img
          onLoad={(e: any) => {
            onPostsItemload(e, pey);
          }}
          ref={refy}
          className={darkmodeReducer ? "turlightpostdark" : "turlightpostlight"}
          src={`./images/posts/${post.item1}`}
          alt="a superstarz post "
          style={{
            width: "100%",
            height: itemheight[pey],
            position: "relative",
            display: "block",
            padding: "0px",
            objectFit: "contain",
          }}
        />
        {/*///////////////////////////////////////////////////////////////////////////POST DATA*/}

        {/*//////////////////////////////////////////////////////////////POSTINFO POSTINFO POSTINFO*/}

        {darkmodeReducer ? (
          <>
            {" "}
            <div
              className={
                darkmodeReducer
                  ? "post-background-dark"
                  : "post-background-light"
              }
              style={{
                height: `${postbackheight}px`,
                marginTop: `-${postbackheight}px`,
                position: "relative",
                transition: "all 350ms ease",
                opacity: itemInfoTopShow ? 1 : 0,
              }}
            >
              {/*///////////////////////////////////////////////////////////////////////////CROPED*/}
              <div
                style={{
                  top: `-${itemheighthold[pey]}px`,
                  position: "relative",
                  transition: "all 350ms ease",
                  opacity: 1,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "left",
                  paddingLeft: "20px",
                  zIndex: 1,
                }}
              >
                {itemcroptype[pey] === 1 ? (
                  <Crop54Icon
                    className={
                      darkmodeReducer
                        ? "make-small-icons-clickable-light  dontallowhighlighting   zuperking"
                        : "make-small-icons-clickable-dark  dontallowhighlighting  zuperking"
                    }
                    style={{
                      fontSize: "2.2vw",
                      opacity: itemInfoTopShow
                        ? itemheight[pey] === "auto"
                          ? 0
                          : 0.45
                        : 0,
                    }}
                  />
                ) : itemcroptype[pey] === 2 ? (
                  <CropPortraitIcon
                    className={
                      darkmodeReducer
                        ? "make-small-icons-clickable-light  dontallowhighlighting   zuperking"
                        : "make-small-icons-clickable-dark  dontallowhighlighting  zuperking"
                    }
                    style={{
                      fontSize: "2.2vw",
                      opacity: itemInfoTopShow
                        ? itemheight[pey] === "auto"
                          ? 0
                          : 0.45
                        : 0,
                    }}
                  />
                ) : (
                  <RemoveRedEyeIcon
                    style={{
                      fontSize: "2.2vw",
                      opacity: 0,
                    }}
                  />
                )}
              </div>
              {/*///////////////////////////////////////////////////////////////////////////CROPED*/}
              {/*///////////////////////////////////////////////////////////////////////////VIEWS*/}
              <div
                style={{
                  top: `-${itemheighthold[pey]}px`,
                  position: "relative",
                  transition: "all 350ms ease",
                  opacity: 1,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "right",
                  zIndex: 2,
                  marginTop: "-10px",
                }}
              >
                <RemoveRedEyeIcon
                  className={
                    darkmodeReducer
                      ? "make-small-icons-clickable-light  dontallowhighlighting   zuperking"
                      : "make-small-icons-clickable-dark  dontallowhighlighting  zuperking"
                  }
                  style={{
                    fontSize: "1.8vw",
                    opacity: 0.4,
                    marginLeft: "92.4%",
                  }}
                />
              </div>
              {/*///////////////////////////////////////////////////////////////////////////VIEWS*/}
              {/*///////////////////////////////////////////////////////////////////////////LOVELY*/}
              <div
                style={{
                  top: `-${itemheighthold[pey]}px`,
                  position: "relative",
                  transition: "all 350ms ease",
                  opacity: 0.75,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "right",
                  zIndex: 2,
                  paddingTop: paddingTopemo,
                }}
              >
                <img
                  className={darkmodeReducer ? "turpostDark" : "turpostLight"}
                  src={`./images/emotions/love.png`}
                  alt="a superstarz post "
                  style={{
                    cursor: "pointer",
                    boxShadow: darkmodeReducer
                      ? "0 0 1px #555555"
                      : "0 0 4.5px #aaaaaa",
                    width: fontSizeemo,
                    marginLeft: "93%",
                    height: "auto",
                    padding: "0px",
                    objectFit: "contain",
                    borderRadius: "50%",
                    position: "relative",
                    zIndex: 1,
                  }}
                />
              </div>
              {/*///////////////////////////////////////////////////////////////////////////LOVELY*/}
              {/*///////////////////////////////////////////////////////////////////////////COOL*/}
              <div
                style={{
                  top: `-${itemheighthold[pey]}px`,
                  position: "relative",
                  transition: "all 350ms ease",
                  opacity: 0.75,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "right",
