import React from "react";

function Slide({ content, i, activeSlide }: any) {
  const getWidth = () => window.innerWidth;
  const mm = i * getWidth();
  return (
    <>
      {" "}
      <img
        src={content}
        style={{
          opacity: `${activeSlide === i ? 0.5 : 0.6}`,
          transform: `translateX(-${mm}px)`,
          height: "auto",
          width: "100vw",
          objectFit: "contain",
        }}
      />
    </>
  );
}

export default Slide;
