import React, { useState, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";

import { Grid, Box } from "@material-ui/core";

import { DarkmodeToggleAction } from ".././GlobalActions";
import { matchPc, matchTablet } from "../DetectDevice";
import { SliderBillboard } from "./SliderBillboard";
import AddIcon from "@mui/icons-material/Add";
import { usePalette } from "react-palette";
import { UpdateColorAction } from ".././GlobalActions";
import AddPhotoAlternateIcon from "@mui/icons-material/AddPhotoAlternate";
import LooksOneIcon from "@mui/icons-material/LooksOne";
import LooksTwoIcon from "@mui/icons-material/LooksTwo";
import PanoramaFishEyeIcon from "@mui/icons-material/PanoramaFishEye";
import DownloadDoneIcon from "@mui/icons-material/DownloadDone";

function Connectx({
  postprofiletop,
  optionsClass,
  post,
  profileImagethumbLeft,
  profileImagethumbTop,
  fontOptions,
  PostCon,
  Comment,
  Reaction,
  Profile,
  Mini,
}: any): JSX.Element {
  const { REACT_APP_SUPERSTARZ_URL, REACT_APP_APPX_STATE } = process.env;

  const [Added, setAdded] = useState(100);

  /*///////////////////////////////////////////////////////////////////////////CONNECT CALL BACK END*/
  var vala = {
    myId: idReducer,
    friendId: post.sender,
  };

  const callAddfav = () => {
    setAdded(100);
    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/add_fav`, { values: vala }, {})
      .then((response) => {
        if (response.data.message === "added") {
          setTimeout(() => {
            setAdded(1);
          }, 2000);
        }
      })
      .catch(function (error) {
        alert("post fav error");
      });
  };

  var valax = {
    myId: idReducer,
    friendId: post.sender,
  };

  const callDelfav = () => {
    setAdded(100);
    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/remove_fav`, { values: valax })
      .then((response) => {
        if (response.data.message === "removed") {
          setTimeout(() => {
            setAdded(0);
          }, 2000);
        }
      })
      .catch(function (error) {
        alert("post fav error");
      });
  };

  /*///////////////////////////////////////////////////////////////////////////CONNECT CALL BACK END*/

  return (
    <>
      {PostCon === 1 ? (
        <div
          className="zuperxyinfo"
          style={{
            opacity: 1,
            top: postprofiletop,
            position: "relative",
            display: "flex",
            alignItems: "center",
            justifyContent: "left",
            zIndex: 1,
            paddingLeft: matchPc ? "1.4vw" : matchTablet ? "2.3vw" : "2.1vw",
            height: "0px",
            fontFamily: "Arial, Helvetica, sans-seri",
          }}
        >
          <Grid
            className={`${optionsClass}`}
            style={{
              backgroundColor: `${post.color1}`,
              zIndex: 2,
              left: `${profileImagethumbLeft}px`,
              top: `${profileImagethumbTop}px`,
              opacity: 0.8,
              position: "absolute",
            }}
          >
            {Added === 0 ? (
              <>
                {" "}
                <AddIcon
                  style={{
                    fontSize: fontOptions,
                    color: "#ffffff",
                  }}
                  className="zuperkinginfo "
                />{" "}
              </>
            ) : Added === 100 ? (
              <>
                {" "}
                <PanoramaFishEyeIcon
                  style={{
                    fontSize: fontOptions,
                    color: "#ffffff",
                  }}
                  className="zuperkinginfo  changeOpacity"
                />{" "}
              </>
            ) : (
              <>
                {" "}
                <DownloadDoneIcon
                  style={{
                    fontSize: fontOptions,
                    color: "#ffffff",
                  }}
                  className="zuperkinginfo"
                />{" "}
              </>
            )}
          </Grid>
          <img
            onClick={() => {
              if (Added === 1) {
                callDelfav();
              } else if (Added === 0) {
                callAddfav();
              }
            }}
            ref={profileImageref}
            onLoad={calculateconnectPosition}
            className={darkmodeReducer ? "turpostDark" : "turpostLight"}
            src={`${post.profile_image}`}
            alt="a superstarz post "
            style={{
              cursor: "pointer",
              boxShadow: darkmodeReducer
                ? "0 0 1px #555555"
                : "0 0 3.5px #aaaaaa",
              width: profilewidth,
              height: "auto",
              padding: "0px",
              objectFit: "contain",
              borderRadius: "50%",
              position: "relative",
              zIndex: 1,
            }}
          />
        </div>
      ) : null}
    </>
  );
}

export const Connect = React.memo(Connectx);
