import React, { useRef, useState } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import "./profile.css";
import { Billboard } from "./Billboard";
import { useSpring, animated } from "react-spring";

function Profilex() {
  const optionsImages = ["1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.jpg"];

  const optionsCollectWidthRef = useRef<HTMLDivElement>(null);
  const optionsCollectImageRef = useRef<HTMLImageElement>(null);

  const [optionsImageDimention, setoptionsImageDimention] = useState(0);
  const [optionsTranslateDimension, setoptionsTranslateDimension] = useState(0);
  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );

  var PaperStyleReducer = "";

  if (darkmodeReducer) {
    PaperStyleReducer = PaperStyleDark;
  } else {
    PaperStyleReducer = PaperStyleLight;
  }

  const [slideStateOptions, setSlideStateOptions] = useState({
    activeSlide: 0,
    translate: 0,
    transition: 0.45,
  });

  console.log(optionsImageDimention);
  const { translate, transition, activeSlide } = slideStateOptions;

  ///
  ///
  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 300,
    },
    transform: `translateX(-${translate}px)`,
    transition: "0.45s",
    height: "auto",
    display: "flex",
    width: `100%`,
    overflow: "hidden",
  });

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM IMAGE
  const sliderFirstImageOnLoad = (item: number) => {
    if (item === 0) {
      if (
        optionsCollectWidthRef.current &&
        optionsCollectWidthRef.current.clientWidth
      ) {
        setoptionsImageDimention(
          optionsCollectWidthRef.current.clientWidth / 1.5
        );

        if (
          optionsCollectImageRef.current &&
          optionsCollectImageRef.current.clientWidth
        ) {
          setoptionsTranslateDimension(
            optionsCollectImageRef.current.clientWidth
          );

          setTimeout(function () {}, 1000);
        }
      }
    }
  };

  ///
  ///
  ///
  return (
    <>
      <Paper
        style={{
          backgroundImage: PaperStyleReducer,
          height: "200vh",
          borderRadius: "0px",
        }}
      >
        {" "}
        <Billboard />
        <Grid container className="dontallowhighlighting">
          <Grid
            item
            component={Box}
            display={{ xs: "none", md: "block" }}
            md={2}
          ></Grid>

          <Grid ref={optionsCollectWidthRef} item xs={1} md={1}></Grid>
          <Grid item xs={4} md={2}></Grid>
          <Grid
            item
            xs={7}
            md={5}
            style={{ height: "150px", overflow: "hidden" }}
          >
            <Grid
              ref={optionsCollectImageRef}
              item
              xs={12}
              style={{
                position: "relative",
                width: "auto",
                height: "auto",
                margin: "0 auto",
                overflow: "hidden",
                marginLeft: "0px",
              }}
            >
              <animated.div style={modalanimation}>
                {optionsImages.map((im: any, i: any) => (
                  <img
                    className={
                      darkmodeReducer ? `turprofileDark` : ` turprofileLight`
                    }
                    onLoad={() => {
                      sliderFirstImageOnLoad(i);
                    }}
                    style={{
                      width: `${optionsImageDimention}px`,
                      height: `${optionsImageDimention}px`,
                      borderRadius: "50%",
                      padding: "3px",
                      objectFit: "cover",
                      marginLeft: "13px",
                      marginTop: "15px",
                      boxShadow: "0 0 3.8px blue",
                      marginBottom: "2px",
                    }}
                    src={`./images/options/${im}`}
                  />
                ))}
              </animated.div>
            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </>
  );
}

export const Profile = React.memo(Profilex);
