import { animated, useSpring } from "react-spring";
import { Grid, DialogContent } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { IServerError } from "./appFolder-Interfaces";

export default function ServerError({
  serverErrorDisplay,
  serverErrorData,
  darkmode,
}: IServerError) {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING SERVER ERROR
  const serverErrorAnimation = useSpring({
    config: {
      duration: 500,
    },
    opacity: serverErrorDisplay,
    transform: serverErrorData ? `translateY(0%)` : `translateY(-100%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#222222";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "turlight";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  return (
    <>
      <DialogContent
        className=" dontallowhighlighting   "
        style={{
          padding: "0px",
          position: "fixed",
          top: "0em",
          zIndex: 2,
          height: "auto",
          width: "100%",
        }}
      >
        <animated.div style={serverErrorAnimation}>
          <Grid
            xs={12}
            className="turlight"
            style={{
              width: "100%",
              display: "flex",
              height: "40px",
              backgroundColor: "#eeeeee",
              marginTop: "0.3px",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: "#000000",
                fontSize: "1.1vw",
                display: "flex",
                alignContent: "center",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "bolder",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: "red",
                    fontSize: "1.8vw",
                  }}
                />
              </Grid>
              <Grid style={{ opacity: 0.87 }} xs={10}>
                {serverErrorData}
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      </DialogContent>
    </>
  );
}
