import React, { useState, useCallback, useRef } from "react";

import {
  IconButton,
  InputAdornment,
  TextField,
  DialogContent,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";
import { ItextfIeldLogin } from "./log-Interfaces";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import { useSelector } from "react-redux";
import CircularProgress from "@material-ui/core/CircularProgress";
import { IPasswordCheck } from "./log-Interfaces";

function PasswordCheckx({
  widthHolder,
  checkSignupPassword,
  setcheckSignupPassword,
  checkSignupPasswordACTIVATE,
  setcheckSignupPasswordACTIVATE,
  signmeup,
}: IPasswordCheck): JSX.Element {
  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const [showSpinner, setshowSpinner] = useState<boolean>(false);
  const confirmPaswordTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  /////
  var sizex: "small" | "medium" | undefined = undefined;
  var font1 = " ";
  var font2 = " ";
  var transform = "";
  var widthcheckPassword = "";
  var heighttt = " ";
  var positionn: any = "";
  var bottomm = "";
  var displayy = "";

  ///////

  if (matchPc) {
    var heighttt = "100vh";
    sizex = "medium";
    font1 = "2.7vh";
    font2 = "1.9vh";
    transform = "scale(1)";
    widthcheckPassword = widthHolder;
    positionn = "";
    bottomm = "";
    displayy = "none";
  } else if (matchTablet) {
    var heighttt = "95.2vh";
    sizex = "small";
    font1 = "2.6vh";
    font2 = "2vh";
    transform = "scale(1)";
    widthcheckPassword = "62%";
    positionn = "fixed";
    bottomm = "0em";
    displayy = "block";
  } else {
    var heighttt = "95.2vh";
    sizex = "small";
    font1 = "";
    font2 = "";
    transform = "scale(0.94)";
    widthcheckPassword = "100%";
    positionn = "fixed";
    bottomm = "0em";
    displayy = "block";
  }

  ///
  ///
  ///
  /// SANITISE LOG IN FORM INPUT CLIENT SIDE
  const updatecheckSignupPasswordvalues = useCallback(
    (e: any) => {
      const { value } = e.target;
      setcheckSignupPassword(value);
      if (confirmPaswordTimer.current) {
        clearTimeout(confirmPaswordTimer.current);
      }
      if (!showSpinner) {
        setshowSpinner(true);
      }
      confirmPaswordTimer.current = setTimeout(() => {
        signmeup(value);
        setshowSpinner(false);
      }, 2000);
    },

    [checkSignupPassword, signmeup, showSpinner]
  );

  var spinColor = "";
  var topp = "";
  var toptext = "";
  var spinsize: number = 60;
  var left = "";
  darkmodeReducer ? (spinColor = "#333333") : (spinColor = "#aaaaaa");
  if (matchPc) {
    topp = "11em";
    toptext = "-17vh";
    spinsize = 55;
    left = "42%";
  } else if (matchTablet) {
    topp = "35em";
    toptext = "-22vh";
    spinsize = 76;
    left = "45%";
  } else {
    topp = "10em";
    toptext = "-31vh";
    spinsize = 60;
    left = "42%";
  }

  ///
  ///
  ///
  ///MATERIAL UI SPINNER STYLES (INSPIRED BY FACEBOOK SPINNERS)
  const useStylesmodalformsignup = makeStyles((theme) => ({
    root: {
      position: "relative",
      textAlign: "center",
      left: 0,
    },
    bottom: {
      color: theme.palette.grey[theme.palette.type === "light" ? 300 : 700],
    },
    top: {
      color: spinColor,
      animationDuration: "550ms",
      position: "absolute",
      left: 0,
    },
    circle: {
      strokeLinecap: "round",
    },
  }));

  const classes = useStylesmodalformsignup();

  return (
    <>
      {checkSignupPasswordACTIVATE ? (
        <>
          <DialogContent
            className={
              darkmodeReducer
                ? " dontallowhighlighting  mobileTextfield-backplateColorDark"
                : " dontallowhighlighting  mobileTextfield-backplateColorLight"
            }
            style={{
              zIndex: 12,
              display: displayy,
              bottom: "0em",
              height: "95.5vh",
              overflow: "hidden",
              width: "100%",
              position: "fixed",
              marginTop: "-3px",
            }}
          ></DialogContent>
          <Grid
            item
            xs={12}
            style={{
              position: "fixed",
              top: topp,
              left: left,
              zIndex: 14,
            }}
          >
            <div
              className={classes.root}
              style={{
                opacity: showSpinner ? "0.3" : "0",
              }}
            >
              <CircularProgress
                variant="determinate"
                className={classes.bottom}
                size={spinsize}
                thickness={4}
                value={100}
              />
              <CircularProgress
                variant="indeterminate"
                disableShrink
                className={classes.top}
                classes={{
                  circle: classes.circle,
                }}
                size={spinsize}
                thickness={4}
              />
            </div>{" "}
          </Grid>
          <TextField
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            style={{
              transform: transform,
              width: widthcheckPassword,
              position: "relative",
              top: toptext,
              zIndex: 14,
            }}
            label="Confirm Password"
            type="text"
            onChange={updatecheckSignupPasswordvalues}
            name="inputedUsername"
            value={checkSignupPassword}
            variant="standard"
          />
        </>
      ) : null}
    </>
  );
}

export const PasswordCheck = React.memo(PasswordCheckx);
