import React, { useState, useCallback, useEffect, useRef } from "react";

import Axios from "axios";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
/// USE DISPATCH
import { useSelector, useDispatch } from "react-redux";
import Palette, { usePalette } from "react-palette";
import CheckIcon from "@mui/icons-material/Check";
import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { UpdateColorAction } from "../GlobalActions";

function AboutColorx({ zoomed }: any): JSX.Element {
  const dispatch = useDispatch();
  ///

  const [se, setse] = useState<any>(null);

  var ColorData = [
    "#FF0000",
    "#FFA500",
    "#FFFF00",
    "#DFFF00.",
    "#00FF00.",
    "#00FF7F",
    "#00FFFF",
    "#0080FF",
    "#0000FF",
    "#8F00FF",
    "#FF00FF",
    "#FF007F",
  ];

  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  var showCheck1: any = "hidden";
  var showCheck2: any = "hidden";
  var showCheck3: any = "hidden";
  var showCheck4: any = "hidden";
  var showCheck5: any = "hidden";

  if (colortypeReducer === 0) {
    showCheck1 = "visible";
  } else if (colortypeReducer === 1) {
    showCheck2 = "visible";
  } else if (colortypeReducer === 2) {
    showCheck3 = "visible";
  } else if (colortypeReducer === 3) {
    showCheck4 = "visible";
  } else {
    showCheck5 = "visible";
  }
  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      id: number;
    };
  }
  const { image, id } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const idReducer = id;

  const imageReducer = image;

  ///
  ///
  ///
  /// REACT PALLETTE (VIBRANTCOLOR DETECTION)
  ////  const { data, loading, error } = usePalette(`./images/profile/${imageReducer}`);

  const { data } = usePalette(se);

  ///
  ///
  ///
  ///DISPATCH

  ///
  ///
  ///
  ///DOT ENV DATA
  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  var colorboy: any = {
    color1: "",
    color2: "",
    colortype: 0,
    id: idReducer,
  };

  const updateColor = useCallback(
    (type: number) => {
      if (type === 0) {
        colorboy = {
          color1: data.lightVibrant,
          color2: data.darkVibrant,
          colortype: 0,
          id: idReducer,
        };
      } else if (type === 1) {
        colorboy = {
          color1: data.vibrant,
          color2: "",
          colortype: 1,
          id: idReducer,
        };
      } else if (type === 2) {
        colorboy = {
          color1: darkmodeReducer ? data.darkVibrant : data.lightVibrant,
          color2: "",
          colortype: 2,
          id: idReducer,
        };
      } else if (type === 3) {
        colorboy = {
          color1: data.muted,
          color2: "",
          colortype: 3,
          id: idReducer,
        };
      } else {
        colorboy = {
          color1: darkmodeReducer ? data.darkMuted : data.lightMuted,
          color2: "",
          colortype: 4,
          id: idReducer,
        };
      }

      Axios.put(
        `${REACT_APP_SUPERSTARZ_URL}/update_color`,
        { values: colorboy },
        {}
      )
        .then((response) => {
          if (response.data.message === "color updated") {
            dispatch(UpdateColorAction(colorboy));
          }
        })
        .catch(function (error) {
          alert("about color error");
        });
    },
    [REACT_APP_SUPERSTARZ_URL, dispatch, data, colorboy]
  );

  const updatecolor1 = () => {
    updateColor(0);
  };

  const updatecolor2 = () => {
    updateColor(1);
  };

  const updatecolor3 = () => {
    updateColor(2);
  };

  const updatecolor4 = () => {
    updateColor(3);
  };

  const updatecolor5 = () => {
    updateColor(4);
  };

  const initialRawSignValue = {
    inputedEmail: "",
    inputedUsername: "",
    inputedPassword: "",
  };
  const initialErrorSignValue = {
    inputedEmail: 0,
    inputedUsername: 0,
    inputedPassword: 0,
  };

  const [rawSignupValues, setRawSignupValues] = useState(initialRawSignValue);
  const [errorsSignupValues, setErrorsSignupValues] = useState(
    initialErrorSignValue
  );

  const UsernameCheckingTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  const clickcolortimer = useRef<ReturnType<typeof setTimeout> | null>(null);

  if (clickcolortimer.current) {
    clearTimeout(clickcolortimer.current);
  }
  clickcolortimer.current = setTimeout(() => {}, 3000);

  const updateTextFeildValues = useCallback(() => {}, [
    REACT_APP_SUPERSTARZ_URL,
    dispatch,
    data,
    colorboy,
  ]);

  return (
    <>
      <animated.div style={animationop}>
        <Grid
          container
          className={
            typeTop
              ? darkmodeReducer
                ? `optionsTop-background-dark `
                : `optionsTop-background-light `
              : ""
          }
          onTouchStart={handleTouchStartOptions}
          onTouchMove={handleTouchMoveOptions}
          style={{
            zIndex: 1,
            padding: "0px",
            top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
            position: "relative",
            margin: "0 auto",
            overflow: "hidden",
            left: "0px",
            height: typeTop ? (matchPc ? "24.4vh" : "20.5vh") : "auto",
            paddingBottom: "1.4vh",
          }}
        >
          <animated.div ref={optionsCollectImageRef} style={modalanimation}>
            <Grid
              item
              style={{
                margin: "auto",
                textAlign: "center",
                position: "relative",
                bottom: typeTop ? "-2.6vh" : "0.2em",
                left: typeTop ? "-3px" : matchPc ? "-2px" : "0px",
              }}
            >
              <CircleIcon
                onClick={() => {
                  nextSlidePc();
                }}
                className="buttonshake"
                style={{
                  fontSize: typeTop ? "1.7vw" : "1.3vw",
                  cursor: "pointer",
                  color: darkmodeReducer
                    ? typeTop
                      ? "rgba(200, 200, 200, 0.5)"
                      : "rgba(200, 200, 200, 0.1)"
                    : typeTop
                    ? "rgba(005, 005, 005, 0.4)"
                    : "rgba(005, 005, 005, 0.2)",
                  display: matchPc ? "block" : "none",
                }}
              />
            </Grid>
            {optionsImages.map((im: any, i: any) => (
              <Grid key={i} item xs={12}>
                {typeTop ? (
                  <Grid
                    item
                    xs={12}
                    style={{
                      margin: "auto",
                      textAlign: "center",
                      position: "relative",
                      top: "2.2vh",
                    }}
                  >
                    <Grid
                      item
                      xs={12}
                      style={{
                        paddingBottom: "10px",
                        fontSize: matchPc
                          ? "1.1vw"
                          : matchTablet
                          ? "2.08vh"
                          : "2vh",
                        fontWeight: "bolder",
                        fontFamily: "Arial, Helvetica, sans-serif",
                        visibility: ActiveSlide === i ? "visible" : "hidden",
                        filter: darkmodeReducer
                          ? "drop-shadow(1.2px 0.1px 1.92px rgba(255, 255, 255, 0.4))"
                          : "drop-shadow(1.2px 0.1px 1.92px rgba(41, 53, 70, 8.35))",
                        color: darkmodeReducer ? "#dddddd" : "#0b111b",
                      }}
                    >
                      {" "}
                      {optionsNameData[i]}
                    </Grid>
                  </Grid>
                ) : null}
                {i === circleIdentify || i === circleIdentify2 ? (
                  <>
                    {i === circleIdentify ? (
                      <>
                        {" "}
                        <div
                          onClick={() => {
                            clickOptions(i, optionsClickType, "upload");
                          }}
                          style={{
                            cursor: ActiveSlide === i ? "pointer" : "copy",
                            width: `${getSliderWidthNew}px`,
                            height: `${getSliderWidthNew}px`,
                            backgroundColor: darkmodeReducer
                              ? "rgba(010,010,010, 0.68)"
                              : "rgba(220,220,220, 0.7)",
                            borderRadius: "50%",
                            marginTop: "2.15vh",
                            textAlign: "center",
                            alignItems: "center",
                            display: "grid",
                            justifyContent: "center",
                            boxShadow: darkmodeReducer
                              ? ActiveSlide === i
                                ? colortypeReducer === 0
                                  ? `0 0 5.5px ${colorReducerdark}`
                                  : `0 0 5.5px ${colorReducer}`
                                : typeTop
                                ? "0 0 5.5px#aaaaaa"
                                : ""
                              : ActiveSlide === i
                              ? `0 0 5.5px ${colorReducer}`
                              : typeTop
                              ? "0 0 5.45px#222222"
                              : ``,
                          }}
                        >
                          {" "}
                          <SettingsIcon
                            style={{
                              fontSize: matchPc ? "2vw" : "5vh",
                              opacity: ActiveSlide === i ? 0.5 : 1,
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        </div>
                      </>
                    ) : (
                      <>
                        {" "}
                        <div
                          onClick={() => {
                            clickOptions(i, optionsClickType, "upload");
                          }}
                          style={{
                            cursor: ActiveSlide === i ? "pointer" : "copy",
                            width: `${getSliderWidthNew}px`,
                            height: `${getSliderWidthNew}px`,
                            backgroundColor: darkmodeReducer
                              ? "rgba(010,010,010, 0.68)"
                              : "rgba(220,220,220, 0.7)",
                            borderRadius: "50%",
                            marginTop: "2.15vh",
                            textAlign: "center",
                            alignItems: "center",
                            display: "grid",
                            justifyContent: "center",
                            boxShadow: darkmodeReducer
                              ? ActiveSlide === i
                                ? colortypeReducer === 0
                                  ? `0 0 5.5px ${colorReducerdark}`
                                  : `0 0 5.5px ${colorReducer}`
                                : typeTop
                                ? "0 0 5.5px#aaaaaa"
                                : ""
                              : ActiveSlide === i
                              ? `0 0 5.5px ${colorReducer}`
                              : typeTop
                              ? "0 0 5.45px#222222"
                              : ``,
                          }}
                        >
                          {" "}
                          <CircleIcon
                            style={{
                              fontSize: matchPc ? "1.65vw" : "4.5vh",
                              opacity: ActiveSlide === i ? 0.5 : 1,
                              color: darkmodeReducer
                                ? ActiveSlide === i
                                  ? "red"
                                  : "#eeeeee"
                                : ActiveSlide === i
                                ? "red"
                                : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        </div>
                      </>
                    )}
                  </>
                ) : (
                  <img
                    alt={` ${optionsNameData[i]}  option`}
                    onClick={() => {
                      clickOptions(i, optionsClickType, " ");
                    }}
                    style={{
                      cursor: ActiveSlide === i ? "pointer" : "copy",
                      width: `${getSliderWidthNew}px`,
                      height: `${getSliderWidthNew}px`,
                      borderRadius: "50%",
                      padding: "0px",
                      objectFit: "cover",
                      marginLeft: "2px",
                      marginTop: "14px",
                      opacity: ActiveSlide === i ? 0.5 : 1,
                      boxShadow: darkmodeReducer
                        ? ActiveSlide === i
                          ? colortypeReducer === 0
                            ? `0 0 6.8px ${colorReducerdark}`
                            : `0 0 6.8px ${colorReducer}`
                          : typeTop
                          ? "0 0 12.5px#aaaaaa"
                          : ""
                        : ActiveSlide === i
                        ? `0 0 5.7px ${colorReducer}`
                        : typeTop
                        ? `0 0 14.45px#222222`
                        : "",

                      marginBottom: "2.2px",
                    }}
                    src={`./images/options/${im}`}
                  />
                )}

                {typeTop ? null : (
                  <Grid
                    item
                    xs={12}
                    style={{
                      margin: "auto",
                      textAlign: "center",
                      position: "relative",
                      bottom:
                        i === circleIdentify || i === circleIdentify2
                          ? matchPc
                            ? "-0.5em"
                            : "-0.3em"
                          : "0.2em",
                    }}
                  >
                    <Grid
                      item
                      xs={12}
                      style={{
                        fontSize: matchPc
                          ? "1.1vw"
                          : matchTablet
                          ? "2vh"
                          : "1.8vh",
                        fontWeight: "bolder",
                        fontFamily: "Arial, Helvetica, sans-serif",
                        visibility: ActiveSlide === i ? "visible" : "hidden",
                        opacity: typeTop ? 0 : optinstopshowingReducer ? 0 : 1,
                        color: darkmodeReducer ? "#dddddd" : "#0b111b",
                      }}
                    >
                      {" "}
                      {optionsNameData[i]}
                    </Grid>
                  </Grid>
                )}
              </Grid>
            ))}
          </animated.div>
        </Grid>
      </animated.div>


      
      <Grid container>
        <Grid
          item
          xs={12}
          style={{
            marginTop: zoomed ? "6.5vh" : "5vh",
            padding: "0px",
            marginLeft: zoomed ? "9.4vw" : "2.3vw",
            zIndex: 6,
          }}
        >
          {/*/////////////////////////////////////////////////////////////////////////// 1*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "0%",
              visibility: showCheck1,
            }}
          >
            <div
              style={{
                fontFamily: "Arial, Helvetica, sans-serif",
                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon
                style={{
                  fontSize: matchPc ? "1.1vw" : "2.08vh",
                  fontWeight: 900,
                }}
              />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "0%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            onClick={updatecolor1}
            style={{
              textAlign: "center",
              alignItems: "center",
              display: "grid",
              justifyContent: "center",
              margin: "auto",
              width: `8vh`,
              height: `8vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 1,
              marginLeft: "0.25vw",
              marginTop: "0.5vh",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////1*/}
          {/*///////////////////////////////////////////////////////////////////////////2*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "7%",
              visibility: showCheck2,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            onClick={updatecolor2}
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "7%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////2*/}
          {/*///////////////////////////////////////////////////////////////////////////3*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "14%",
              visibility: showCheck3,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            onClick={updatecolor3}
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "14%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////3*/}
          {/*///////////////////////////////////////////////////////////////////////////4*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "21%",
              visibility: showCheck4,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            onClick={updatecolor4}
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "21%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>{" "}
          {/*///////////////////////////////////////////////////////////////////////////4*/}
          {/*///////////////////////////////////////////////////////////////////////////5*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "28%",
              visibility: showCheck5,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            onClick={updatecolor5}
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: "#00ccff",
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "28%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////5*/}
        </Grid>{" "}
      </Grid>
    </>
  );
}

export const AboutColor = React.memo(AboutColorx);
