import { animated, useSpring } from "react-spring";
import { Grid, DialogContent } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { IServerError } from "./appFolder-Interfaces";

export default function ServerError({
  setServerErrorData,
  serverErrorDisplay,
  serverErrorData,
  darkmode,
}: IServerError) {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING SERVER ERROR
  const serverErrorAnimation = useSpring({
    config: {
      duration: 500,
    },
    opacity: serverErrorDisplay,
    transform: serverErrorData ? `translateY(0%)` : `translateY(-100%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorColor = "rgb(34, 34, 34, 0.05)";
    ErrorTextColor = "#ffffff";
    ErrorIconColor = "orange";
  } else {
    ErrorColor = "rgb(238, 238, 238 0.05)";
    ErrorTextColor = "#000000";
    ErrorIconColor = "red";
  }

  let Backtype = "";
  darkmode ? (Backtype = "od") : (Backtype = "ol");

  return (
    <>
      <DialogContent
        onClick={() => setServerErrorData(null)}
        className={
          serverErrorData ? ` dontallowhighlighting ${Backtype}` : `display-off`
        }
        style={{
          padding: "0px",
          position: "fixed",
          top: "0em",
          zIndex: 2,
          height: "100%",
          width: "100%",
        }}
      >
        <animated.div style={serverErrorAnimation}>
          <Grid
            xs={12}
            className=""
            style={{
              width: "100%",
              display: "flex",
              height: "50px",
              backgroundColor: ErrorColor,
              marginTop: "0.3px",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "1.19vw",
                display: "flex",
                alignContent: "center",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "bolder",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  className="element-bop2"
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                    fontSize: "1.8vw",
                    WebkitFilter: `drop-shadow(1.9px 0.1px 0.3px ${ErrorIconColor})`,
                    filter: `drop-shadow(1.9px 0.1px 0.3px ${ErrorIconColor})`,
                  }}
                />
              </Grid>
              <Grid
                style={{ opacity: 1, textAlign: "left", display: "flex" }}
                xs={10}
              >
                {serverErrorData}, Please Try Again{" "}
                <span
                  className="zuperxy"
                  style={{
                    fontSize: "129%",
                  }}
                >
                  &#128524;
                </span>
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      </DialogContent>
    </>
  );
}
