import React, {
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { PreviewCanvas } from "./PreviewCanvas";
import Slider from "@material-ui/core/Slider";
import { UpdateNavFilterReducer } from "../GlobalActions";
import { UpdateNavCropReducer } from "../GlobalActions";
import { styled } from "@mui/system";
import { Grid, Box } from "@material-ui/core";

function CropUploadModex({
  CropSaved,
  ActiveSlide,
  handleTouchStartOptions,
  handleTouchMoveOptions,
  optionsCollectImageRef,
  modalanimation,
  getSliderWidthNew,
  optionsClickType,
  clickOptions,
  optionsImages,
  optionsNameData,
  cropTOPLEVELScrollRef,
  refWithimageData,
  filterImage,
  setfilterImage,
  setActivatefilterImage,
  selectedImage,
  setselectedImage,
  itemUploadRef,
  closeUploadModalx,
  cropscrollRef,
}: any): JSX.Element {
  const [matchTabletMobile, setmatchTabletMobile] = useState<boolean>(false);

  const dispatch = useDispatch();
  var extendxy = 1;
  extendxy = matchTabletMobile ? 0.57 : 2.4;

  ///
  ///

  const [zoom, setzoom] = useState<any>(1);

  const [
    longimageForcroppercanvassizechange,
    setlongimageForcroppercanvassizechange,
  ] = useState<any>("true");

  const [zoomReal, setzoomReal] = useState<any>(1);

  const [getCropHeightmultiplier, setgetCropHeightmultiplier] =
    useState<any>(0.8);

  const [cropADDY, setcropADDY] = useState<number>(1);

