import React, { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Grid, DialogContent } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { ImodalFormLoginError } from "./appFolder-Interfaces";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import LinearProgress from "@material-ui/core/LinearProgress";

export default function ModalFormLoginError({
  type,
  ErrorDisplay,
  darkmode,
  WidthHolder,
}: ImodalFormLoginError) {
  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalLogError = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    transform: ErrorDisplay ? `translateY(0%)` : `translateY(100%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#222222";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "turlight";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  return (
    <>
      <DialogContent
        className="dialog-container dontallowhighlighting "
        style={{}}
      >
        <animated.div style={animationModalLogError}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "-1.5px",
              display: "flex",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid style={{ opacity: 0.7 }} xs={10}></Grid>
            </Grid>
          </Grid>
        </animated.div>
      </DialogContent>
    </>
  );
}
