import React, {
    useRef,
    useEffect,
    useCallback,
    useState,
    useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import { UpdateUploadData } from "../GlobalActions";
import DoneIcon from '@mui/icons-material/Done';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CircleIcon from "@mui/icons-material/Circle";
import CropIcon from '@mui/icons-material/Crop';
import Slider from "@material-ui/core/Slider";
import { TaskbarCrop } from "./TaskbarCrop";
import set from "date-fns/set/index.js";
import TitleIcon from '@mui/icons-material/Title';
import TouchAppIcon from '@mui/icons-material/TouchApp';
import InsertPhotoIcon from '@material-ui/icons/InsertPhoto';
import RestoreIcon from '@material-ui/icons/Restore';






function TaskbarSuperphotoImageViewx({ seeOriginal, AddMainImageRef, index, MainImageFiltersRef, finalImage, ray, Originalray, setOriginalRay }: any): JSX.Element {


    const [fade, setfade] = useState(false);



    const [Big, setBig] = useState(false);

    useEffect(() => {

        setfade(true)

        setTimeout(() => {
            setfade(false)
        }, 2000);



    }, [MainImageFiltersRef.current])

    ///
    ///
    ///
    /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
    interface RootStateGlobalReducer {
        GlobalReducer: {
            darkmode: boolean;
            screenHeight: number;
        };
    }

    ///
    ///
    ///
    /// GET DARKMODE FROM REDUX STORE
    const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
        ...state.GlobalReducer,
    }));
    const darkmodeReducer = darkmode;



    const Timer = useRef<ReturnType<typeof setTimeout> | null>(null);

    return (
        <>


            <Grid container>

                <Grid item xs={12} style={{
                    cursor: 'pointer',


                }}>



                    <img
                        onClick={() => { seeOriginal(index) }}
                        className={ray[index] === 1 ? 'fadeboyinFilter' : ""} ref={AddMainImageRef} style={{ cursor: 'pointer', width: '100%' }} src={
                            MainImageFiltersRef.current[index]
                                ? MainImageFiltersRef.current[index].src
                                : null
                        }
                        alt={`Image ${index}`} />


                    <TitleIcon
                        className={
                            darkmodeReducer
                                ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                                : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
                        }
                        style={{
                            fontSize: '2.5vw',
                            position: 'relative',
                            bottom: '80vh',
                            left: '88%',
                            borderRadius: '50%',
                            zIndex: 2000,
                            transition: "transform 0.1s",
                            cursor: 'pointer',
                            opacity: '0.8'


                        }}
                    />

                    <InsertPhotoIcon
                        className={
                            darkmodeReducer
                                ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                                : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
                        }
                        style={{
                            fontSize: '2.5vw',
                            position: 'relative',
                            bottom: '70vh',
                            left: '80%',
                            borderRadius: '50%',
                            zIndex: 2000,
                            transition: "transform 0.1s",
                            cursor: 'pointer',
                            opacity: '0.8'


                        }}
                    />


                    <TouchAppIcon
                        className={
                            darkmodeReducer
                                ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                                : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
                        }
                        style={{
                            fontSize: '2.5vw',
                            position: 'relative',
                            bottom: '60vh',
                            left: '72%',
                            borderRadius: '50%',
                            zIndex: 2000,
                            transition: "transform 0.1s",
                            cursor: 'pointer',
                            opacity: '0.8'


                        }}
                    />



                    <RestoreIcon
                        className={
                            darkmodeReducer
                                ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                                : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
                        }
                        style={{
                            fontSize: '2.5vw',
                            position: 'relative',
                            bottom: '50vh',
                            left: '64%',
                            borderRadius: '50%',
                            zIndex: 2000,
                            transition: "transform 0.1s",
                            cursor: 'pointer',
                            opacity: '0.8'


                        }}
                    />






                </Grid>
            </Grid>





        </>
    );
}

export const TaskbarSuperphotoImageView = React.memo(TaskbarSuperphotoImageViewx);
