import React, { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";

export default function ModalLogFormError(ErrorDisplay) {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationxx = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    transform: ErrorData ? `translateY(0%)` : `translateY(100%)`,
  });

  return (
    <animated.div style={animationxx}>
      <Grid
        xs={12}
        style={{
          width: "82%",
          height: "34px",
          color: "#ffffff",
          backgroundColor: "#222222",
          margin: "auto",
          borderTopLeftRadius: "20px",
          borderTopRightRadius: "20px",
          marginBottom: "-1.5px",
          display: "flex",
        }}
      >
        <Grid
          xs={12}
          style={{
            color: "#eeeeee",
            fontSize: "0.85vw",
            display: "flex",
            alignItems: "center",
            paddingRight: "15px",
            justifyContent: "flex-end",
            textAlign: "left",
            fontWeight: "bold",
            fontFamily: "sans-serif",
          }}
        >
          <ErrorOutlineIcon
            style={{
              marginLeft: "8px",
              marginRight: "12px",
              color: "orange ",
            }}
          />

          {ErrorData}
        </Grid>
      </Grid>
    </animated.div>
  );
}
