import React, { useState, useEffect } from "react";

import Slide from "./Slide";
import Arrow from "./Arrow";
import Dots from "./Dots";
import { Paper, Grid } from "@material-ui/core";
import { useSpring, animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const [index, set] = useState(0);
  const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const transitions = useTransition(index, {
    key: index,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 1000 },
  });

  /**  useEffect(() => {
    const t = setInterval(
      () => set((state) => (state + 0) % slides.length),
      2000
    );
    return () => clearTimeout(t);
  }, []);    */

  const nextSlide = () => {
    set((state) => (state + 1) % slides.length);
  };

  const prevSlide = () => {
    if (index === 0) {
      set(slides.length - 1);
    } else {
      set(index - 1);
    }
  };

  return (
    <Grid className="flex fill center">
      {transitions((style, i) => (
        <div style={{ height: "auto", width: getWidth() }}>
          <animated.img
            style={{
              ...style,
              height: "auto",
              width: "inherit",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
            }}
            src={slides[i]}
          />
        </div>
      ))}{" "}
      <Arrow direction="left" clickSlide={prevSlide} />
      <Arrow direction="right" clickSlide={nextSlide} />
      <Dots slides={slides} activeSlide={index} />
    </Grid>
  );
};

export default Slider;
