import React, {
    useRef,
    useEffect,
    useCallback,
    useState,
    useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import { UpdateUploadData } from "../GlobalActions";
import DoneIcon from '@mui/icons-material/Done';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CircleIcon from "@mui/icons-material/Circle";
import CropIcon from '@mui/icons-material/Crop';
import Slider from "@material-ui/core/Slider";
import { TaskbarCrop } from "./TaskbarCrop";
import set from "date-fns/set/index.js";
import TitleIcon from '@mui/icons-material/Title';
import TouchAppIcon from '@mui/icons-material/TouchApp';
import InsertPhotoIcon from '@material-ui/icons/InsertPhoto';
import RestoreIcon from '@material-ui/icons/Restore';







function TaskbarTextx({ ShowText, setShowText, activeItem, setactiveItem, MainImageFiltersRef }: any): JSX.Element {





    const TextCanvasRef: any = useRef(null);

    const canvasRefdummy: any = useRef(null);



    ///
    ///
    ///
    /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
    interface RootStateGlobalReducer {
        GlobalReducer: {
            darkmode: boolean;
            screenHeight: number;
        };
    }

    ///
    ///
    ///
    /// GET DARKMODE FROM REDUX STORE
    const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
        ...state.GlobalReducer,
    }));
    const darkmodeReducer = darkmode;


    const [CropImageHolder, setCropImageHolder] = useState<any>(null);


    const [Dimension, setDimension] = useState<any>(260);

    const [imageHeight, setimageHeight] = useState(1300);
    const [imageWidth, setimageWidth] = useState(1300);




    useLayoutEffect(() => {
        if (ShowText) {


            const Newcropimage: any = new Image();
            Newcropimage.src = MainImageFiltersRef.current[activeItem].src;
            Newcropimage.onload = function () {
                if (CropImageHolder === Newcropimage) {

                } else {

                    //////////////
                    setCropImageHolder(Newcropimage);

                    let newWidth = Dimension;
                    let newHeight = Dimension;
                    const aspectRatio = Newcropimage.naturalWidth / Newcropimage.naturalHeight;

                    if (newWidth / newHeight > aspectRatio) {
                        newWidth = newHeight * aspectRatio;
                    } else {
                        newHeight = newWidth / aspectRatio;
                    }


                    setimageHeight(newHeight);
                    setimageWidth(newWidth);
                    ///GiveImageSpecificDimensions(Newcropimage);


                }



            }
        }
    }, [MainImageFiltersRef, Dimension, ShowText]);



    const callCanvas = useCallback(() => {
        if (CropImageHolder) {

            const ctx = TextCanvasRef.current.getContext("2d");

            ctx.clearRect(0, 0, TextCanvasRef.current.width, TextCanvasRef.current.height);

            const w = imageWidth;
            const h = imageHeight;

            TextCanvasRef.current.width = w;
            TextCanvasRef.current.height = h;


            /// setProcessingRay(updatedProcessingRay);
            ////////////////////////////////

            requestAnimationFrame(async () => {
                try {


                    ctx.drawImage(CropImageHolder, 0, 0);

                }
                catch (e: any) {
                    alert(e);
                    console.log(e)
                }
            });

        }
    }, [CropImageHolder, TextCanvasRef, imageHeight, imageWidth])




    useLayoutEffect(() => {

        callCanvas();

    }, [CropImageHolder])





    return (
        <>
            <Grid container>

                <Grid item xs={12} style={{

                    height: '200vh', width: '100%', position: 'fixed', top: '0vh', zIndex: 0, backgroundColor: darkmodeReducer
                        ? "rgb(0,0,0,0.8)"
                        : "rgb(255,255,255,0.8)"
                }}>



                </Grid>


                <canvas
                    ref={TextCanvasRef}
                    style={{
                        padding: "0px",
                        margin: "auto",
                        cursor: "pointer",
                        position: 'fixed',
                        top: "0vh",
                        zIndex: 10
                    }} />

            </Grid>

        </>
    );
}

export const TaskbarText = React.memo(TaskbarTextx);
