import React, {
    useRef,
    useEffect,
    useCallback,
    useState,
    useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import { UpdateUploadData } from "../GlobalActions";
import DoneIcon from '@mui/icons-material/Done';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

import CropIcon from '@mui/icons-material/Crop';
import Slider from "@material-ui/core/Slider";
import { TaskbarSuperphotoImageView } from "./TaskbarSuperphotoImageView";
import set from "date-fns/set/index.js";
import { Hidden } from "@mui/material";










function TaskbarSuperphotox({
    finalImage, getSliderWidth, ratiox, hidecropper }: any): JSX.Element {

    const [ray, setRay] = useState([]);




    useEffect(() => {
        var cc = ratiox === 1 ? '1vh' : ratiox === 2 ? '-5vh' : '-17vh';
        setMarginTopFilters(cc);

    }, [ratiox]);



    useEffect(() => {
        /// setFilterThumbGo(false);
        if (hidecropper) {
            setcanvasData(finalImage[0]);
            if (FilterThumbGo) { } else {
                setTimeout(() => {
                    setcanvasData([]);
                    setcanvasData(finalImage[0]);

                    // setDimension(200);

                    setfiltenumm(0);
                    setfiltemode(1);
                    setfilterType(UploadFilterNameData[0]);

                    setFilterThumbGo(true);
                }, 1000)
            }
        }
    }, [hidecropper]);


    const imageFiltersRef = useRef<any>([]);

    const MainImageFiltersRef = useRef<any>([]);





    const AddMainImageRef = (imageRef: any) => {
        if (imageRef && !MainImageFiltersRef.current.includes(imageRef)) {
            MainImageFiltersRef.current.push(imageRef);
        }
    }


    const addimageFiltersRef = (imageRef: any) => {
        if (imageRef && !imageFiltersRef.current.includes(imageRef)) {
            imageFiltersRef.current.push(imageRef);
        }
    }


    const callMain = (ty: number) => {
        setDimension(1180);


        for (let i = 0; i < 1; i++) {
            var xx = i * 100;
            setTimeout(() => {
                const ctx = canvasRefdummy.current.getContext("2d");
                ctx.clearRect(0, 0, canvasRefdummy.current.width, canvasRefdummy.current.height);



                setfilterType(UploadFilterNameData[ty]);
                setfiltenumm(i);
                setfiltemode(0);

                setcanvasData([]);
                setcanvasData(finalImage[i]);

            }, xx)

        }

    }






    const [imageHeight, setimageHeight] = useState(1300);
    const [imageWidth, setimageWidth] = useState(1300);



    const dispatch = useDispatch();



    const [canvasData, setcanvasData] = useState<any>(null);

    const [MarginTopFilters, setMarginTopFilters] = useState('');

    const [FilterThumbGo, setFilterThumbGo] = useState(false);

    const [filterType, setfilterType] = useState('normal');
    const [filtenumm, setfiltenumm] = useState(0);
    const [filtemode, setfiltemode] = useState(0);


    const [mode, setmode] = useState(0);

    const [CropImageHolder, setCropImageHolder] = useState<any>(null);


    const [Dimension, setDimension] = useState<any>(300);


    const [showAll, setshowAll] = useState(false);


    const Draw = (type: any, typeNum: number, mode: number, ctx: any, w: any, h: any, method: any) => {

        if (type === "lift") {
            ctx.filter =
                "contrast(1.25) brightness(0.98) blur(0px) saturate(130%)";
        } else if (type === "sahara") {
            ctx.filter = "contrast(1.02) brightness(0.9) saturate(104%)";
        } else if (type === "jentle") {
            ctx.filter =
                "contrast(0.9) brightness(0.77) hue-rotate(20deg) saturate(90%) ";
        } else if (type === "mint") {
            ctx.filter = "contrast(1) brightness(1.05)  saturate(155%)  ";
        } else if (type === "nebula") {
            ctx.filter = "contrast(1.15) brightness(1.06)";
        } else if (type === "juice") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.17) brightness(0.98) saturate(185%) blur(0px)";
            } else {
                ctx.filter =
                    "contrast(1) brightness(0.99) saturate(185%) blur(0.55px)";
            }
        } else if (type === "rainbow") {
            ctx.filter = "contrast(1.2) brightness(0.9) saturate(145%) ";
        } else if (type === "superstar") {
            ctx.filter =
                "contrast(1.2) brightness(0.81) saturate(130%)  hue-rotate(1.4deg)";
        } else if (type === "fog") {
            ctx.filter = "contrast(1.2) brightness(1) saturate(137%)";
        } else if (type === "moonshine") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.3) brightness(0.99) saturate(50%) blur(0.8px)";
            } else {
                if (matchMobile) {
                    ctx.filter =
                        "contrast(1) brightness(0.99) saturate(40%) blur(1.2px)";
                } else {
                    ctx.filter =
                        "contrast(0.85) brightness(0.99) saturate(45%) blur(4px)";
                }
            }
        } else if (type === "vintage") {
            ctx.filter = " contrast(1.2)  brightness(0.83)  saturate(22%)";
        } else {
        }

        ctx.drawImage(CropImageHolder, 0, 0, w, h);

        //////////////////////VIGNETTE
        var width = w;
        var height = h;
        if (type === "sahara") {
            var gradient = ctx.createLinearGradient(
                w / 2.7,
                h / 2,
                0,
                w / 4.8,
                h / 2,
                w * 8.65
            );

            gradient.addColorStop(0, "rgba(80, 111, 32, 0.1)");
            gradient.addColorStop(1, "rgba(10,10,10,0.05)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "mint") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.65
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.03)");
            gradient.addColorStop(1, "rgba(0,0,0,0.23)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "lift" || type === "juice") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 3,
                0,
                w / 2,
                h / 3,
                w * 0.66
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.01)");
            gradient.addColorStop(1, "rgba(255, 255, 255,0.002)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "moonshine") {
            var gradient = ctx.createLinearGradient(
                w / 2,
                h / 3,
                0,
                w / 2,
                h / 3,
                w * 0.4
            );

            gradient.addColorStop(0, " rgba(255, 255, 255,0.001)");
            gradient.addColorStop(1, "#403d6149");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);

            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.3
            );

            gradient.addColorStop(0, "rgba(0,0,0,0.02)");
            gradient.addColorStop(1, " rgba(255, 255, 255,0.001)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "vintage") {
            var gradient = ctx.createLinearGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.6
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.0001)");
            gradient.addColorStop(1, "rgba(255, 255, 255,0.0001)");
            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "nebula") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.65
            );

            gradient.addColorStop(0, "rgba(255, 255, 255, 0.006)");
            gradient.addColorStop(1, "rgba(0, 0, 0, 0.08)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "rainbow") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.68
            );

            gradient.addColorStop(0, "rgb(163, 218, 37,0.004)");
            gradient.addColorStop(1, "rgb(27, 194, 236,0.004)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "fog") {
            var gradient = ctx.createLinearGradient(
                w / 2,
                h / 71,
                0,
                w / 19,
                h / 2,
                w * 0.4
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0)");
            gradient.addColorStop(1, "#20422e5a");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else {
        }
        //////////////////////VIGNETTE

        //////////////////////GRADIENT BRIGHTNESS

        if (type === "normal") {
        } else {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.6
            );
            if (type === "lift") {
                gradient.addColorStop(0, "#dcecdf16");
                gradient.addColorStop(1, "#dceaec1a");
            } else if (type === "fog") {
                gradient.addColorStop(0, "#c9d6df11");
                gradient.addColorStop(1, "#bef0ce13");
            } else if (type === "jentle") {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.003)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.003)");
            } else if (type === "vintage") {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.001)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.003)");
            } else if (
                type === "superstar" ||
                type === "rainbow" ||
                type === "sahara" ||
                type === "mint"
            ) {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.006)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.005)");
            } else {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.02)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.01)");
            }

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        }

        //////////////////////GRADIENT BRIGHTNESS

        ////////////////// /////// GLOBALCOMPOSITEOPERATION
        if (type === "sahara") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#a58e89cc";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#a58e89cc";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "jentle") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#d3b5b5";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#d3b5b5";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "mint") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#25302f18";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#25302f18";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "nebula") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#6e6b72cc";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#6c6971cc";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "moonshine") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#77479f68";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69359368";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "juice") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ////////////composition
        } else if (type === "rainbow") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#3950681f";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#42806c1f";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "superstar") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69758113";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69758113";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "fog") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#2c2b2937";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#2c2b2937";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "vintage") {
            ////////////composition

            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#8888b14e";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5c83944e";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#8888b14e";
            ctx.fillRect(0, 0, width, height);
            ////////////composition
        } else {
        }
        ////////////////// /////// GLOBALCOMPOSITEOPERATION

        ////////////////////GRADIENT BLENDING
        if (
            type === "jentle" ||
            type === "mint" ||
            type === "nebula" ||
            type === "juice" ||
            type === "rainbow" ||
            type === "superstar" ||
            type === "fog" ||
            type === "moonshine" ||
            type === "vintage" ||
            type === "sahara"
        ) {
            var gradient = FilterGradient(width, height, type);
            var screen = blend(
                ctx,
                gradient,
                width,
                height,
                function (bottomPixel: any, topPixel: any) {
                    return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
                }
            );
            ctx.putImageData(screen, 0, 0);
        }


        const dataUrl = cropCanvasRef.current.toDataURL('image/png');



        if (mode === 0) {

            MainImageFiltersRef.current[typeNum].src = dataUrl;

            //setDimension(600);

            if (finalImage.length === typeNum) { }
            else if (typeNum === 0) {
                setfilterType(type);
                setfiltenumm(1);
                setfiltemode(0);

                setcanvasData(finalImage[1]);
                //// callCanvas(type, 1, 0);
            } else {
                setfilterType(type);
                setfiltenumm(2);
                setfiltemode(0);


                setcanvasData(finalImage[2]);
                /// callCanvas(type, 2, 0);
            }



        }
        else {

            if (FilterThumbGo) {
                var pp = typeNum + 1;

                if (UploadFilterNameData.length - 1 === typeNum) {

                    imageFiltersRef.current[typeNum].src = dataUrl;

                    callMain(0);

                    setTimeout(() => { setshowAll(true) }, 500)



                }
                else {

                    imageFiltersRef.current[typeNum].src = dataUrl;
                    // setDimension(200);
                    const ctx = canvasRefdummy.current.getContext("2d");
                    ctx.clearRect(0, 0, canvasRefdummy.current.width, canvasRefdummy.current.height);
                    setcanvasData([]);
                    setcanvasData(finalImage[0]);

                    setfilterType(UploadFilterNameData[pp]);
                    setfiltenumm(pp);
                    setfiltemode(1);

                }

            }


        }




        const ctxx = cropCanvasRef.current.getContext("2d");
        ctxx.clearRect(0, 0, cropCanvasRef.current.width, cropCanvasRef.current.height);


        ////////////////////GRADIENT BLENDING
    }






    useLayoutEffect(() => {
        if (canvasData) {
            const ctx = cropCanvasRef.current.getContext("2d");
            ctx.clearRect(0, 0, cropCanvasRef.current.width, cropCanvasRef.current.height);

            const Newcropimage: any = new Image();
            Newcropimage.src = canvasData;
            Newcropimage.onload = function () {
                if (CropImageHolder === Newcropimage) {
                } else {

                    //////////////
                    setCropImageHolder(Newcropimage);

                    let newWidth = Dimension;
                    let newHeight = Dimension;
                    const aspectRatio = Newcropimage.naturalWidth / Newcropimage.naturalHeight;

                    if (newWidth / newHeight > aspectRatio) {
                        newWidth = newHeight * aspectRatio;
                    } else {
                        newHeight = newWidth / aspectRatio;
                    }


                    setimageHeight(newHeight);
                    setimageWidth(newWidth);
                    ///GiveImageSpecificDimensions(Newcropimage);


                }


            }
        }
    }, [canvasData, Dimension]);




    const cropCanvasRef: any = useRef(null);

    const canvasRefdummy: any = useRef(null);

    var filz = 'superstar';

    var filll = 'lift';

    var filj = 'juice';

    var filf = 'rainbow';

    var filt = 'fog';

    var filff = 'sahara';

    var filn = 'nebula';

    var filjjj = 'jentle';

    var film = 'mint';

    var films = 'moonshine';

    var fil = 'vintage';



    const callCanvas = useCallback(() => {
        if (CropImageHolder) {

            const ctx = cropCanvasRef.current.getContext("2d");

            ctx.clearRect(0, 0, cropCanvasRef.current.width, cropCanvasRef.current.height);

            const w = imageWidth;
            const h = imageHeight;

            cropCanvasRef.current.width = w;
            cropCanvasRef.current.height = h;


            ////////////////////////////////

            requestAnimationFrame(async () => {
                try {
                    Draw(filterType, filtenumm, filtemode, ctx, w, h, '');

                }
                catch (e: any) {
                    alert(e);
                    console.log(e)
                }
            });

        }
    }, [CropImageHolder, cropCanvasRef, imageHeight, imageWidth, mode, filterType, filtenumm, filtemode])


    useLayoutEffect(() => {
        ///  FilterThumb();




        callCanvas();

    }, [CropImageHolder])







    function blend(
        background: any,
        foreground: any,
        width: any,
        height: any,
        transform: any
    ) {
        var bottom = background.getImageData(0, 0, width, height);
        var top = foreground.getImageData(0, 0, width, height);

        for (var i = 0, size = top.data.length; i < size; i += 4) {
            // red
            top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
            // green
            top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
            // blue
            top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
            // the fourth slot is alpha. We don't need that (so skip by 4)
        }

        return top;
    }







