import React, { useState, useRef, useCallback, useEffect } from "react";
import { Menu } from "./Menu";
import { Billboard } from "./Billboard";
import { Post } from "./Post";
import { OptionsSlider } from "./OptionsSlider";
import { CommentTemplate } from "../CommentTemplate";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import {
  Paper,
  Grid,
  Typography,
  createTheme,
  MuiThemeProvider,
  Box,
} from "@material-ui/core";
import ImagePhotoSizeSelectSmall from "material-ui/svg-icons/image/photo-size-select-small";
import Masonry from "@mui/lab/Masonry";
import AddIcon from "@mui/icons-material/Add";
import { Upload } from "../upload/Upload";

function Profilex({
  OpenModalForm,
  getSliderWidthRef,
  getSliderWidth,
  postData,
  addPostItemsRef,
  onPostsItemload,
  itemheight,
  itemheighthold,
  postbackheight,
  formtype,
  showModalForm,
  CloseModalForm,
  aboutTemplateGo,
  commentTemplateGo,
  itemcroptype,
  itemFinalPostHeight,
  onPostsItemClicked,
  itemCLICKED,
  addpostDivRef,
  postDatainner,
  itemOriginalPostHeight,
  ActiveAutoPlay,
  setActiveAutoPlay,
  AUTOSlideLongImages,
  postDivRef,
  paperPostScrollRef,
}: any) {
  const dispatch = useDispatch();

  const [HidePostDataOnScroll, setHidePostDataOnScroll] =
    useState<boolean>(false);

  ///
  ///
  ///TYPES FOR ISLOGGEDIN
  interface RootStateIsLogged {
    IsLoggedReducer: {
      loggedIn: boolean;
    };
  }

  ///
  ///LOGGED IN DATA FROM REDUX
  const { loggedIn } = useSelector((state: RootStateIsLogged) => ({
    ...state.IsLoggedReducer,
  }));
  const loggedInReducer = loggedIn;

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );
  var PaperStyleReducer = "";

  if (darkmodeReducer) {
    PaperStyleReducer = PaperStyleDark;
  } else {
    PaperStyleReducer = PaperStyleLight;
  }

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  var widthProfilePic = matchPc ? "72%" : matchTablet ? "85%" : "44vw";
  var topProfilePic = matchPc ? "-20vh" : matchTablet ? "-12vh" : "-8vh";
  var leftProfilePic = matchPc ? "1vw" : matchTablet ? "3.5vw" : "2.7vw";

  var optionsClass = "";
  var fontOptions = "";

  if (matchPc) {
    optionsClass = "profile-optionsImagePc";
    fontOptions = "2.7vw";
  } else if (matchTablet) {
    optionsClass = "profile-optionsImageTablet";
    fontOptions = "5rem";
  } else {
    optionsClass = "profile-optionsImageMobile";
    fontOptions = "1.9rem";
  }

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
    };
  }
  const { image } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const imageReducer = image;

  const blank = () => {};

  interface IappVariables {
    shade: string;
    shade2: string;
    shade2num: string;
    shade2nump: string;
    secondarymaincolor: string;
    maincolor: string;
    shade2nump22: string;
    littleTextColor: string;
  }

  var appVariables: IappVariables = {
    shade: "",
    shade2: "",
    shade2num: "",
    shade2nump: "",
    secondarymaincolor: "",
    maincolor: "",
    shade2nump22: "",
    littleTextColor: "",
  };

  var appVariablesDARK: IappVariables = {
    shade: "#cccccc",
    shade2: "#ffffff",
    shade2num: "1.1",
    shade2nump: "1.8",
    secondarymaincolor: "#dddddd",
    maincolor: "#dddddd",
    shade2nump22: "5.5",
    littleTextColor: "#dddddd",
  };

  var appVariablesLIGHT: IappVariables = {
    shade: "#0b111b",
    shade2: "#0b111b",
    shade2num: "1.5",
    shade2nump: "1.5",
    secondarymaincolor: "#0b111b",
    maincolor: "#0b111b",
    shade2nump22: "8",
    littleTextColor: "#0b111b",
  };

  ///
  ///
  ///
  ///CONDITIONAL STATEMENT FOR DARKMODE
  if (darkmodeReducer) {
    appVariables = appVariablesDARK;
  } else {
    appVariables = appVariablesLIGHT;
  }
  ///
  ///
  ///
  ///MATERIAL UI  THEME CUSTOMIZATAION
  let themeGeneralSettings = createTheme({
    palette: {
      primary: {
        main: `${appVariables.secondarymaincolor}`,
      },
      secondary: {
        main: `${appVariables.secondarymaincolor}`,
      },
      type: darkmodeReducer ? "dark" : "light",
    },
  });

  const [showModal, setShowModal] = useState<boolean>(false);

  ///
  ///
  ///
  /// CLOSE MODAL (STARTS AN ONPOPSTATE EVENT)
  const closemodal = useCallback((backbutton: number) => {
    //pop states fires when back and forward buttons used
    if (backbutton === 1) {
      window.onpopstate = () => {
        window.history.pushState(null, "", window.location.href);
        window.onpopstate = null;
        setShowModal(false);
      };
    } else {
      window.history.pushState(null, "", ".");
      window.onpopstate = null;
      setShowModal(false);
    }
  }, []);

  ///
  ///
  ///
  ///OPEN MODAL THEN CALL CLOSEMODAL FUNCTION WHICH WAITS FOR A POP EVENT(for closing modal)
  const Openmodal = useCallback(() => {
    setShowModal(!showModal);
    //pushstate add enteries to your history
    window.history.pushState(null, "", "Options");
    closemodal(1);
  }, [showModal, closemodal]);

  return (
    <>
      <MuiThemeProvider theme={themeGeneralSettings}>
        <Menu
          Openmodal={Openmodal}
          setHidePostDataOnScroll={setHidePostDataOnScroll}
          getSliderWidth={getSliderWidth}
          paperPostScrollRef={paperPostScrollRef}
        />
        <Billboard OpenModalForm={OpenModalForm} />
        <Grid container className="dontallowhighlighting">
          <Grid item xs={12} style={{ padding: "0px", height: "0px" }}></Grid>
          <Grid
            item
            ref={getSliderWidthRef}
            xs={2}
            md={1}
            style={{ padding: "0px" }}
          ></Grid>
          <Grid item xs={12} style={{ padding: "0px", height: "0px" }}></Grid>
          <Grid
            item
            component={Box}
            display={{ xs: "none", md: "block" }}
            md={2}
          ></Grid>

          <Grid item xs={5} md={3} style={{ padding: "0px" }}>
            {" "}
            <Grid
              item
              xs={12}
              style={{
                position: "relative",
                width: widthProfilePic,
                height: "auto",
                marginLeft: leftProfilePic,
                top: topProfilePic,
                zIndex: 3,
              }}
            >
              <Grid
                className={`  ${optionsClass}   `}
                style={{
                  zIndex: 2,
                  backgroundColor:
                    colortypeReducer === 0
                      ? darkmodeReducer
                        ? colorReducerdark
                        : colorReducer
                      : colorReducer,

                  opacity: 0.7,
                }}
              >
                <AddIcon
                  style={{
                    fontSize: fontOptions,
                    color: "#ffffff",
                  }}
                  className="zuperkinginfo"
                />
              </Grid>
              <img
                onClick={OpenModalForm}
                className={
                  darkmodeReducer
                    ? `turprofileDark image-zoom-on-click`
                    : ` turprofileLight image-zoom-on-click`
                }
                style={{
                  cursor: "pointer",
                  position: "absolute",
                  zIndex: 0,
                  textAlign: "left",
                  objectFit: "contain",
                  width: "100%",
                  borderRadius: "50%",
                  margin: "auto",
                  filter: "blur(1.3px)",
                }}
                src={`./images/profilethumb/${imageReducer}`}
                alt="Superstarz Billboard "
              />{" "}
              <img
                onClick={OpenModalForm}
                className={
                  darkmodeReducer
                    ? `turprofileDark image-gray-on-click`
                    : ` turprofileLight image-gray-on-click`
                }
                style={{
                  cursor: "pointer",
                  position: "relative",
                  zIndex: 1,
                  objectFit: "contain",
                  width: "100%",
                  borderRadius: "50%",
                  margin: "auto",
                }}
                src={`./images/profile/${imageReducer}`}
                alt="Superstarz Billboard "
              />
            </Grid>
          </Grid>

          <Grid
            item
            xs={7}
            md={5}
            style={{
              position: "relative",
              height: "19.5vh",
              paddingLeft: matchPc ? "25px" : matchTablet ? "42px" : "24px",
              marginTop: matchTablet ? "10px" : "-4px",
              zIndex: 1,
            }}
          >
            <OptionsSlider
              OptionsSliderType="Menu"
              OpenUploadModal={OpenUploadModal}
              sethaltedTop={blank}
              typeTop={false}
              getSliderWidth={getSliderWidth}
            />
          </Grid>

          <Grid
            item
            xs={12}
            style={{
              padding: "0px",
              height: "0px",
              marginTop: matchPc ? "-13.5vh" : matchTablet ? "-5vh" : "-4vh",
            }}
          >
            {postData ? (
              <Masonry columns={matchPc ? 2 : 1} spacing={0}>
                {postData.map((post: any, i: any) => (
                  <div key={i} style={{ position: "relative" }}>
                    <Post
                      key={i}
                      pey={i}
                      addPostItemsRef={addPostItemsRef}
                      postDivRef={postDivRef}
                      onPostsItemload={onPostsItemload}
                      post={post}
                      itemheight={itemheight}
                      itemheighthold={itemheighthold}
                      postbackheight={postbackheight}
                      itemcroptype={itemcroptype}
                      length={postData.length}
                      itemFinalPostHeight={itemFinalPostHeight}
                      onPostsItemClicked={onPostsItemClicked}
                      itemCLICKED={itemCLICKED}
                      addpostDivRef={addpostDivRef}
                      postDatainner={postDatainner}
                      itemOriginalPostHeight={itemOriginalPostHeight}
                      ActiveAutoPlay={ActiveAutoPlay}
                      setActiveAutoPlay={setActiveAutoPlay}
                      AUTOSlideLongImages={AUTOSlideLongImages}
                      HidePostDataOnScroll={HidePostDataOnScroll}
                    />
                  </div>
                ))}
              </Masonry>
            ) : null}
          </Grid>
        </Grid>
        <CommentTemplate
          formtype={formtype}
          showModalForm={showModalForm}
          CloseModalForm={CloseModalForm}
          aboutTemp={aboutTemplateGo}
          commentTemp={commentTemplateGo}
        />
        <Upload showModal={showModal} closemodal={closemodal} />
      </MuiThemeProvider>
    </>
  );
}

export const Profile = React.memo(Profilex);
