import React, { useRef, useEffect, useCallback } from "react";
import AddRoundedIcon from "@material-ui/icons/AddRounded";
import { Grid, Switch, Box } from "@material-ui/core";
import { useSpring, animated } from "react-spring";
import * as CSS from "csstype";

interface IOption {
  closemodal: (backbutton: number) => void;
  showModal: boolean;
  setShowModal: (showModal: boolean) => void;
  darkmode: boolean;
  setDarkmode: (darkmodex: boolean) => void;
  marginData: CSS.Properties;
  fontData: CSS.Properties;
  switchsize: "medium" | "small" | undefined;
}

var toggleDarkMode: boolean = false;

export default function OptionInnerModal({
  closemodal,
  showModal,
  setShowModal,
  darkmode,
  setDarkmode,
  marginData,
  fontData,
  switchsize,
}: IOption): JSX.Element {
  /*SETS DARKMODE FORM LOCAL STORAGE IF NOT EMPTY */
  useEffect(() => {
    let themelocaldata = JSON.parse(localStorage.getItem("darkmode")!);
    if (themelocaldata !== null) {
      setDarkmode(themelocaldata);
    }
  }, [setDarkmode]);

  /*USE SLIDE DOWN ANIMATION FROM REACT SPRING */
  const animation = useSpring({
    config: {
      duration: 2500,
    },
    opacity: showModal ? 1 : 0.9,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`,
  });

  /* USEREF TARGETS A DIV(BACKGROUND) AND CLOSES MODAL ON CLICK*/
  const inputRef = useRef<HTMLInputElement>(null);

  const onBackgroundFocus = (e: any): void => {
    if (inputRef.current === e.target) {
      closemodal(0);
    }
  };

  /*ESCAPE KEY CLOSE MODAL */
  const escapePress = useCallback(
    (e) => {
      if (e.key === "Escape" && showModal) {
        closemodal(0);
      }
    },
    [setShowModal, showModal]
  );
  useEffect(() => {
    document.addEventListener("keydown", escapePress);
    return () => document.removeEventListener("keydown", escapePress);
  }, [escapePress]);
  /*ESCAPE */

  const An = animated(Grid);
  return (
    <div>
      {showModal ? (
        <Grid container xs={12} className="theme-more-contain">
          <Grid
            item
            component={Box}
            display={{ xs: "block", sm: "block" }}
            sm={10}
            xs={7}
          ></Grid>

          <Grid
            item
            xs={2}
            sm={1}
            className="switchopacity  zuperking"
            style={marginData}
          >
            <Switch
              size={switchsize}
              checked={darkmode}
              onChange={() => {
                toggleDarkMode = !darkmode;
                setDarkmode(!darkmode);
                localStorage.setItem("darkmode", toggleDarkMode.toString());
              }}
            />
          </Grid>
          <Grid
            item
            component={Box}
            display={{ xs: "none", sm: "none" }}
          ></Grid>
          <Grid
            item
            xs={3}
            className="dontallowselect"
            sm={1}
            style={{ textAlign: "right" }}
          >
            '
            <animated.div style={animation}>
              <AddRoundedIcon
                className={
                  darkmode
                    ? "make-small-icons-clickable-dark  dontallowselect  "
                    : "make-small-icons-clickable-light  dontallowselect  "
                }
                fontSize="large"
                style={fontData}
              />
            </animated.div>
          </Grid>
        </Grid>
      ) : null}
    </div>
  );
}
