import React, { useState, useRef } from "react";
import Arrow from "./Arrow";
import Dots from "./Dots";
import SliderNumber from "./SliderNumber";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const [sliderIndex, setSliderIndex] = useState(0);
  const [sliderIndexSlow, setSliderIndexSlow] = useState(0);

  /// const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const waitChangeIndexTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const handleTouchMoveTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  const SlideimageRef = useRef<HTMLImageElement>(null);
  const SlideimageRefthumb = useRef<HTMLImageElement>(null);

  const [imageHeight, setImageHeight] = useState(0);

  ///
  ///
  ///
  /// SPRING TRANSITION WITH INDEX
  const transitions = useTransition(sliderIndex, {
    key: sliderIndex,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 2950 },
  });

  ///
  ///
  ///
  /// SPRING TRANSITION WITH INDEX
  const sliderFirstImageOnLoad = (item: number) => {
    if (item === 0) {
      if (SlideimageRef.current && SlideimageRef.current.clientHeight) {
        setImageHeight(SlideimageRef.current.clientHeight);
      }
    }
  };

  const sliderFirstImageOnLoadthumb = (item: number) => {
    if (item === 0 && imageHeight === 0) {
      if (
        SlideimageRefthumb.current &&
        SlideimageRefthumb.current.clientHeight
      ) {
        setImageHeight(SlideimageRefthumb.current.clientHeight);
      }
    }
  };

  const [touchPosition, setTouchPosition] = useState(null);

  const handleTouchStart = (e: any) => {
    e.preventDefault();
    ////onMouseDown onMouseMove
    ////touchDown = e.clientX
    const touchDown = e.touches[0].clientX;
    setTouchPosition(touchDown);
  };

  const handleTouchMove = (e: any) => {
    e.preventDefault();
    if (handleTouchMoveTimer.current) {
      clearTimeout(handleTouchMoveTimer.current);
    }
    handleTouchMoveTimer.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }
      ////currentTouch = e.clientX
      const currentTouch = e.touches[0].clientX;
      const diff = touchDown - currentTouch;

      if (diff > 40) {
        nextSlide();
      } else if (diff < -40) {
        prevSlide();
      } else {
      }

      setTouchPosition(null);
      return false;
    }, 200);
    return false;
  };

  const waitChangeIndex = (a: number) => {
    if (waitChangeIndexTimer.current) {
      clearTimeout(waitChangeIndexTimer.current);
    }
    waitChangeIndexTimer.current = setTimeout(function () {
      setSliderIndexSlow(a);
    }, 500);
  };

  const nextSlide = () => {
    ///set((state) => (state + 1) % slides.length);
    if (sliderIndex === slides.length - 1) {
      setSliderIndex(0);
      waitChangeIndex(0);
    } else {
      setSliderIndex(sliderIndex + 1);
      waitChangeIndex(sliderIndex + 1);
    }
  };

  const prevSlide = () => {
    if (sliderIndex === 0) {
      setSliderIndex(slides.length - 1);
      waitChangeIndex(slides.length - 1);
    } else {
      setSliderIndex(sliderIndex - 1);
      waitChangeIndex(sliderIndex - 1);
    }
  };

  const GotoDots = (clickedDot: number) => {
    setSliderIndex(clickedDot);
    waitChangeIndex(clickedDot);
  };

  /* useEffect(() => {
    const t = setInterval(() => set(state => (state + 1) % slides.length), 4000)
    return () => clearTimeout(t)
  }, []) */

  return (
    <Grid
      item
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      xs={12}
      style={{
        position: "relative",
        height: `${imageHeight}px`,
        opacity: imageHeight === 0 ? 0 : 1,
      }}
    >
      <SliderNumber activeSlide={sliderIndexSlow} total={slides.length} />
      <Arrow
        direction="left"
        clickSlidep={prevSlide}
        clickSliden={nextSlide}
        height={imageHeight}
      />
      {transitions((style, i) => (
        <Grid key={i} xs={12} item>
          <animated.img
            ref={SlideimageRefthumb}
            onLoad={() => {
              sliderFirstImageOnLoadthumb(i);
            }}
            className="turlightbillboard"
            style={{
              ...style,
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
            }}
            src={`./images/thumbs/${slides[i]}`}
          />

          <animated.img
            ref={SlideimageRef}
            onLoad={() => {
              sliderFirstImageOnLoad(i);
            }}
            className="turlightbillboard"
            style={{
              ...style,
              cursor: "pointer",
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
              zIndex: 1,
            }}
            src={`./images/${slides[i]}`}
          />
        </Grid>
      ))}{" "}
      <Dots GotoDots={GotoDots} slides={slides} activeSlide={sliderIndexSlow} />
    </Grid>
  );
};

export default Slider;
