    const sliderLeft = useCallback(() => {

        if (translate > 1) {
            ///setpixels((pixels) => pixels - (2 * window.innerWidth) / 100);
            setTranslate((translate) => translate - getSliderWidthx);
        }
        setActiveSlide((ActiveSlide) => ActiveSlide - 1);
    }, [ActiveSlide, getSliderWidthx, translate]);




    const sliderRight = useCallback(() => {
        if (translate <= getSliderWidthx * 5) {
            setTranslate((translate) => translate + getSliderWidthx);
        }
        setActiveSlide((ActiveSlide) => ActiveSlide + 1);

    }, [ActiveSlide, getSliderWidthx, translate]);






    return (
        <>

            <div style={{ filter: showAll ? 'blur(0px)' : 'blur(40px)', height: '100vh', overflow: 'hidden' }}>




                {finalImage.length > 0 ? <><Masonry
                    columns={finalImage.length}
                    spacing={0}
                    style={{
                        padding: "0px",
                        paddingLeft: finalImage.length === 3 ? '0px' : Pad,
                        paddingRight: finalImage.length === 3 ? '0px' : Pad,
                        overflow: 'hidden',
                        height: '76vh',
                    }}
                >


                    {finalImage.map((picture: any, index: any) => (



                        <TaskbarSuperphotoImageView
                            seeOriginal={seeOriginal}
                            Originalray={Originalray}
                            setOriginalRay={setOriginalRay}
                            ray={ray}
                            finalImage={finalImage} AddMainImageRef={AddMainImageRef} index={index} MainImageFiltersRef={MainImageFiltersRef} />

                    ))}




                </Masonry ></> : null
                }




                <animated.div style={modalanimation}>
                    {optionsImages.map((im: any, i: any) => (
                        <Grid onClick={() => {
                            ////

                            if (ActiveSlide === i) {
                                if (Timer2.current) {
                                    clearTimeout(Timer2.current);
                                }
                                callDrawSlider(i);

                                //////
                            } else {
                                slider(i)
                            }

                        }} key={i} item xs={12} style={{
                            cursor: 'pointer',
                            marginTop: MarginTopFilters, textAlign: 'center', margin: 'auto',
                            visibility: imageFiltersRef.current[i] ? 'visible' : 'hidden'

                        }}>
                            <img

                                ref={addimageFiltersRef}
                                className={darkmodeReducer ? 'turpostLightx' : "turpostDarkx"}
                                style={{
                                    cursor: 'pointer',
                                    borderRadius: "50%",
                                    padding: "0px",
                                    border: ActiveSlide === i ? `2px solid ${colorReducer}` : ``,
                                    objectFit: "cover",
                                    marginLeft: "2vw",
                                    width: getSliderWidth,
                                    height: getSliderWidth,

                                }}
                                src={
                                    imageFiltersRef.current[i]
                                        ? imageFiltersRef.current[i].src
                                        : null
                                }
                            />
                            <span className='zuperxy' style={{ marginLeft: "2vw", fontSize: '2.25vh', color: '#ffffff', fontFamily: "Roboto Condensed" }}> {UploadFilterNameData[i]} </span>
                        </Grid>
                    ))}


                </animated.div >


                <CircleIcon
                    onClick={sliderLeft}
                    onMouseOver={() => {
                        setBigCircle(true);
                    }}
                    onMouseOut={() => {
                        setBigCircle(false);
                    }}

                    className="zuperkingtur"
                    style={{
                        fontSize: '3vh',
                        color: '#00ccff',
                        position: 'absolute',
                        bottom: '2vh',
                        left: '12px',
                        zIndex: 2000,
                        transform: BigCircle ? "scale(2.8)" : "scale(1)",
                        transition: "transform 0.1s",
                        cursor: 'pointer',
                        display: 'none'

                    }}
                />

                <CircleIcon
                    onClick={sliderRight}
                    onMouseOver={() => {
                        setBigCircle2(true);
                    }}
                    onMouseOut={() => {
                        setBigCircle2(false);
                    }}

                    className="zuperkingtur btn"
                    style={{
                        fontSize: '3vh',
                        color: '#00ccff',
                        position: 'absolute',
                        bottom: '2vh',
                        right: '12px',
                        zIndex: 2000,
                        transform: BigCircle2 ? "scale(2.8)" : "scale(1)",
                        transition: "transform 0.1s",
                        cursor: 'pointer',
                        display: 'none'

                    }}
                />





                <Grid item xs={12} style={{
                    cursor: 'pointer',
                    marginTop: MarginTopFilters, textAlign: 'center', margin: 'auto',
                    visibility: 'visible',
                    position: 'fixed',
                    bottom: '30vh',
                    width: '1000',
                    height: '0vh',
                    zIndex: 200000,
                    backgroundColor: '#00ccff',
                    display: showFilterAdjust ? 'block' : 'none'

                }}>
                    <Grid item xs={12} style={{
                        cursor: 'pointer',
                        height: '0px',
                        width: '40%',
                        marginLeft: '37.5vw',
                        display: 'flex',
                        alignItems: 'center',
                        backgroundColor: 'red'
                    }}>


                        <CircleIcon
                            onClick={() => {
                                setfiltercolorMode(0);
                                callMain(ActiveSlide);
                            }}
                            onMouseOver={() => {
                                setBigCircle(true);
                            }}
                            onMouseOut={() => {
                                setBigCircle(false);
                            }}

                            className="zuperkingtur"
                            style={{
                                fontSize: '3vh',
                                marginLeft: '14%',
                                color: colorReducer,
                                transform: BigCircle ? "scale(2.8)" : "scale(1)",
                                transition: "transform 0.1s",
                                cursor: 'pointer',


                            }}
                        />


                        <CircleIcon
                            onClick={() => {
                                setfiltercolorMode(1);
                                callMain(ActiveSlide);
                            }}
                            onMouseOver={() => {
                                setBigCircle2(true);
                            }}
                            onMouseOut={() => {
                                setBigCircle2(false);
                            }}

                            className="zuperkingtur"
                            style={{
                                fontSize: '3vh',
                                marginLeft: '30%',
                                color: colorReducer,
                                transform: BigCircle2 ? "scale(3.2)" : "scale(1.4)",
                                transition: "transform 0.1s",
                                cursor: 'pointer',


                            }}
                        />


                        <CircleIcon
                            onClick={() => {
                                setfiltercolorMode(2);
                                callMain(ActiveSlide);
                            }}
                            onMouseOver={() => {
                                setBigCircle3(true);
                            }}
                            onMouseOut={() => {
                                setBigCircle3(false);
                            }}

                            className="zuperkingtur"
                            style={{
                                fontSize: '3vh',
                                marginLeft: '30%',
                                color: colorReducer,
                                transform: BigCircle3 ? "scale(3.6)" : "scale(1.9)",
                                transition: "transform 0.1s",
                                cursor: 'pointer',


                            }}
                        />


                    </Grid>



                </Grid>





                <canvas
                    ref={cropCanvasRef}
                    style={{
                        padding: "0px",
                        margin: "auto",
                        cursor: "pointer",
                        overflow: 'hidden',
                        position: 'fixed',
                        top: "-400%",
                        zIndex: 0,
                        visibility: "hidden",
                        width: '30%'

                    }} />


                <canvas
                    ref={canvasRefdummy}
                    style={{
                        padding: "0px",
                        display: "none",
                        position: "fixed",
                        top: "-400%",
                        zIndex: 0,
                        visibility: "hidden",
                    }}
                />

            </div>


        </>
    );
}

export const TaskbarSuperphoto = React.memo(TaskbarSuperphotox);
