      if (callAutoPlayAgainTimer.current) {
        clearTimeout(callAutoPlayAgainTimer.current);
      }
      callAutoPlayAgainTimer.current = setTimeout(function () {
        SliderAutoPlay(1);
      }, sliderDuration);
    }
  };

  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const GotoDots = (clickedDot: number) => {
    setSliderIndex((sliderIndex: any) => clickedDot);
    waitChangeIndex(clickedDot, sliderIndex);
  };


  const handleVideoEnd = () => {
    // console.log("Video has ended.");



    if (interacttime.current) {
      clearTimeout(interacttime.current);
    }
    if (interacttime2.current) {
      clearTimeout(interacttime2.current);
    }
    setshowSpin(false);
    if (sc.current) {
      clearTimeout(sc.current);
    }
    setinteract(false);
    setinteractContent("");
    setinteracttypeAll(0);
    setfadeout(false);


    // Add additional logic for when the video ends
  };


  const handleVideoLoad = () => {
    if (videoPlayerReff.current) {
      // Define the handler for when the video ends
      alert('kk');
      // Add the 'ended' event listener to the video element
      videoPlayerReff.current.addEventListener('ended', handleVideoEnd);

      // Start playing the video
      videoPlayerReff.current.play();
    }
  };

  // Remember to clean up the event listener when the component unmounts or when the video source changes
  useEffect(() => {
    return () => {
      if (videoPlayerReff.current) {
        videoPlayerReff.current.removeEventListener('ended', handleVideoEnd);
      }
    };
  }, [interactContent]);








  return (
    <>
      <Grid
        item
        xs={12}
        style={{
          position: "fixed",
          top: "3vh",
          padding: "0px",
          zIndex: "200",
        }}
      >
        {Tutorial ? (
          <Grid
            item
            onClick={() => {
              setTutorial(false);
            }}
            xs={6}
            style={{
              padding: "10px",
              backgroundColor: "#00ccff",
              borderRadius: "8%",
              fontFamily: "sans-serif",
              fontWeight: "bold",
            }}
          >
            Mobile Interaction Still In Development, Coming Soon 
          </Grid>
        ) : null}
      </Grid>

      <Grid
        item
        onTouchStart={In}
        onTouchMove={handleTouchMove}
        xs={12}
        style={{
          position: "relative",
          height: itemCLICKED[pey]
            ? `${itemOriginalPostHeight[pey]}px`
            : type === 1
              ? `${itemOriginalPostHeight[pey]}px`
              : `${itemFinalPostHeight[pey]}px`,
          padding: "0px",
        }}
      >
        <Loader
          post={post}
          autoSlideDisplay={autoSlideDisplay}
          sliderLoader={sliderLoader}
        />
        <div
          onMouseOver={() => {
            setZoomOutBigger(true);
          }}
          onMouseOut={() => {
            setZoomOutBigger(false);
          }}
          style={{
            position: "absolute",
            zIndex: 30,
            left: slides.length > 1 ? 165 : 25,
            cursor: "pointer",
            top: slides.length > 1 ? "2.4vh" : "2.4vh",
            fontFamily: "Arial, Helvetica, sans-serif",
            fontWeight: "bolder",
            opacity: "0.7",
            height: "0px",
            padding: "0px",
          }}
        >
          <span
            onClick={() => {
              setsliderIndexMini(sliderIndex);
              setzoomClickedIndex(pey + 1);
              type === 1 ? setminiProfile(false) : setminiProfile(true);
            }}
            className={darkmodeReducer ? "turlight" : "turdark"}
            style={{
              padding: "1px",
              color: "#ffffff",
            }}
          >
            <ZoomOutIcon
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
              }
              style={{
                fontSize: "2.2vw",
                display: "none",
              }}
            />
          </span>
        </div>{" "}
        {showSpin ? (
          <Grid
            item
            xs={12}
            style={{ position: "absolute", zIndex: 100, padding: "3vh" }}
          >
            <div
              className="spinner zuperxyinfo"
              style={{
                borderTop: `8px solid ${post.color1}`,
                boxShadow: `0 0 8.5px, ${post.color1}`,
              }}
            ></div>
          </Grid>
        ) : null}
        {slides.length > 0 ? (
          interactContent ? null : (
            <SliderNumber
              ActiveCanvas={ActiveCanvas}
              post={post}
              HasInteractivity={HasInteractivity}
              postDatainnerInteraction1={postDatainnerInteraction1}
              postDatainnerInteraction2={postDatainnerInteraction2}
              startSlider={startSlider}
              ActiveAutoPlay={ActiveAutoPlay}
              stopSlider={stopSlider}
              activeSlide={sliderIndexSlow}
              total={slides.length}
              itemCLICKED={itemCLICKED}
              pey={pey}
            />
          )
        ) : null}
        <Arrow
          itemCLICKED={itemCLICKED}
          pey={pey}
          total={slides.length}
          direction="left"
          clickSlideprev={prevSlide}
          clickSlidenext={nextSlide}
          itemOriginalPostHeight={itemOriginalPostHeight}
        />
        {transitions((style, i) => (
          <div key={i}>
            <animated.img
              onLoad={(e: any) => {
                onPostsItemload(e, pey, i);
              }}
              onMouseDown={clickslider}
              ref={addPostItemsRef}
              className={
                darkmodeReducer ? "turlightpostdark" : "turlightpostlight"
              }
              src={slidesThumb[i]}
              alt="a superstarz post "
              style={{
                ...style,
                cursor: "pointer",
                width: "100%",
                height: type === 1 ? `auto` : itemheight[pey],
                position: "absolute",
                padding: "0px",
                objectFit: "cover",
                objectPosition:
                  itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                    ? "50% top"
                    : "50% 50",
                zIndex: 0,


              }}
            />

            <img
              ref={pic}
              onMouseDown={clickslider}
              className={
                darkmodeReducer ? "turlightpostdark" : "turlightpostlight"
              }
              src={slides[i]}
              alt="a clikbate post "
              style={{

                cursor: "pointer",
                width: "100%",
                height: type === 1 ? `auto` : itemheight[pey],
                position: "absolute",
                padding: "0px",
                objectFit: "cover",
                objectPosition:
                  itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                    ? "50% top"
                    : "50% 50",
                zIndex: 1,

              }}
              crossOrigin="anonymous"
            />

            {i === 0 && HasInteractivity && ActiveCanvas === pey ? (
              <canvas
                onMouseUp={(e: any) => {
                  handleTouchStartIn(e, 0);
                }}
                onTouchStart={(e: any) => {
                  handleTouchStartIn(e, 1);
                }}
                ref={canvasRefIn}
                style={{
                  cursor: "pointer",
                  padding: "0px",
                  position: "absolute",
                  zIndex: 11,
                  height: '0px',
                  width: '0px',
                  top: "0vh",
                  margin: "auto",
                  opacity: HideCann ? 0 : 1,
                  visibility: itemCLICKED[pey] ? 'visible' : 'hidden',


                }}
              />
            ) : null}


            {interact && HasInteractivity && ActiveCanvas === pey ? (


              <video
                onClick={() => {

                  if (InteractedDark) {
                    ////////
                    if (InteractTimerxx.current) {
                      clearTimeout(InteractTimerxx.current);
                    }
                    dispatch(UpdateInteract(interactContent, 2));
                    setInteractedDark(false);
                    setinteract(false);
                    setinteractContent("");
                    setinteracttypeAll(0);
                    setfadeout(false);
                    if (sc.current) {
                      clearTimeout(sc.current);
                    }
                    setshowSpin(false);
                    /////////////////
                  } else {
                    setInteractedDark(true);
                    if (InteractTimerxx.current) {
                      clearTimeout(InteractTimerxx.current);
                    }
                    InteractTimerxx.current = setTimeout(() => {
                      setInteractedDark(false);
                      setinteract(false);
                      setinteractContent("");
                      setinteracttypeAll(0);
                      setfadeout(false);
                      if (sc.current) {
                        clearTimeout(sc.current);
                      }
                      setshowSpin(false);
                    }, 1100)

                  }


                }}
                onLoadedData={handleVideoLoad}
                ref={videoPlayerReff}
                src={interactContent}
                style={{
                  filter: InteractedDark ? 'brightness(40%)' : '',
                  cursor: "pointer",
                  height: "100%",
                  objectFit: "contain",
                  zIndex: 50,
                  position: "absolute",
                  margin: "auto",
                  textAlign: "center",
                  top: "0vh",
                  width: "100%",
                  backgroundColor: darkmodeReducer
                    ? "rgb(20,20,20,0.82)"
                    : "rgb(210,210,210,0.77)",
                  visibility: interacttypeAll === 1 ? 'hidden' : 'visible'

                }}></video>
            ) : null}




            {interact && HasInteractivity && ActiveCanvas === pey ? (

              <img
                className={fadeout ? "fadeboyinOut" : "fadeboyinInt"}
                src={interactContent}

                onClick={() => {
                  if (InteractedDark) {
                    ////////
                    if (InteractTimerxx.current) {
                      clearTimeout(InteractTimerxx.current);
                    }
                    dispatch(UpdateInteract(interactContent, 1));
                    setInteractedDark(false);
                    setinteract(false);
                    setinteractContent("");
                    setinteracttypeAll(0);
                    setfadeout(false);
                    if (sc.current) {
                      clearTimeout(sc.current);
                    }
                    setshowSpin(false);
                    /////////////////
                  } else {
                    setInteractedDark(true);
                    if (InteractTimerxx.current) {
                      clearTimeout(InteractTimerxx.current);
                    }
                    InteractTimerxx.current = setTimeout(() => {
                      setInteractedDark(false);
                      setinteract(false);
                      setinteractContent("");
                      setinteracttypeAll(0);
                      setfadeout(false);
                      if (sc.current) {
                        clearTimeout(sc.current);
                      }
                      setshowSpin(false);
                    }, 1100)

                  }


                }}
                style={{
                  filter: InteractedDark ? 'brightness(40%)' : '',
                  cursor: "pointer",
                  height: "100%",
                  objectFit: "contain",
                  zIndex: 50,
                  position: "absolute",
                  margin: "auto",
                  textAlign: "center",
                  top: "0vh",
                  width: "100%",
                  backgroundColor: darkmodeReducer
                    ? "rgb(20,20,20,0.82)"
                    : "rgb(210,210,210,0.77)",
                  visibility: interacttypeAll === 1 ? 'hidden' : 'visible'
                }}
              />
            ) : null}



          </div>
        ))}{" "}
        <Dots
          total={slides.length}
          itemCLICKED={itemCLICKED}
          pey={pey}
          GotoDots={GotoDots}
          slides={slides}
          activeSlide={sliderIndexSlow}
        />
        {post.interact1a && HasInteractivity && ActiveCanvas === pey ? (
          <img
            src={post.interact1a}
            style={{
              height: "100%",
              objectFit: "contain",
              zIndex: 50,
              position: "absolute",
              margin: "auto",
              textAlign: "center",
              top: "-200000vh",
              width: "100%",
            }}
          />
        ) : null}
        {post.interact1b && HasInteractivity && ActiveCanvas === pey ? (
          <img
            src={post.interact1b}
            style={{
              cursor: "pointer",
              height: "100%",
              objectFit: "contain",
              zIndex: 50,
              position: "absolute",
              margin: "auto",
              textAlign: "center",
              top: "-200000vh",
              width: "100%",
            }}
          />
        ) : null}

      </Grid>
    </>
  );
}

export const Slider = React.memo(Sliderx);
