import React, { useState, useRef, useEffect, useCallback } from "react";

import CakeIcon from "@material-ui/icons/Cake";
import RestoreIcon from "@material-ui/icons/Restore";
import { OptionInnerModal } from "./log/OptionInnerModal";
import { useSpring, animated } from "react-spring";
import Axios from "axios";
import { matchPc, matchTablet } from "./DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import {
  Switch,
  DialogContent,
  Paper,
  Grid,
  makeStyles,
  MuiThemeProvider,
} from "@material-ui/core";
import { rememberMeAction, UpdateColorAction } from "./GlobalActions";
import { SuperCheckFeedBack } from "./SuperCheckFeedBack";
///import { SuperBirthdayFeedBack } from "./SuperBirthdayFeedBack";
import lime from "@material-ui/core/colors/lime";
import { createMuiTheme } from "@material-ui/core";
import { ThemeProvider } from "@material-ui/styles";
import "date-fns";
import DateFnsUtils from "@date-io/date-fns";
import { Button } from "@material-ui/core";
import { Scrollbars } from "react-custom-scrollbars";

import {
  DatePicker,
  TimePicker,
  DateTimePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";

import "./log/logCss.css";
import * as CSS from "csstype";

function SupercheckOptionsx({
  showModal,
  closemodal,
  setShowModal,
}: any): JSX.Element {
  const dispatch = useDispatch();

  const [Feedbackshow, setFeedbackshow] = useState<boolean>(false);
  const [selectedDate, setSelectedDate] = useState<any>(new Date());

  ///
  ///
  ///
  /// GET KEEP ME LOGGED IN  STATE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;

  const handleDateChange = (date: any) => {
    setSelectedDate(date);
    alert("date done");
  };

  ///
  ///
  ///
  ///SETS DARKMODE FROM LOCAL STORAGE IF NOT EMPTY
  var color1 = "";
  var color2 = "";
  var color3 = "";
  var color4 = "";

  useEffect(() => {
    if (darkmodeReducer) {
      color1 = "#ff2a7fd4";
      color2 = "#ffd42ad6";
      color3 = "#80b3ffbc";
      color4 = "#00ff66bf";
    } else {
      color1 = "#ff0066ff";
      color2 = "#ccff00ff";
      color3 = "#00ffffff";
      color4 = "#00ff00ff";
    }

    let emojicontrol: number[] = [1, 2, 3, 4];
    var randomemoji =
      emojicontrol[Math.floor(Math.random() * emojicontrol.length)];

    if (randomemoji === 1) {
      dispatch(UpdateColorAction(color1));
    } else if (randomemoji === 2) {
      dispatch(UpdateColorAction(color2));
    } else if (randomemoji === 3) {
      dispatch(UpdateColorAction(color3));
    } else {
      dispatch(UpdateColorAction(color4));
    }
  }, []);

  ///
  ///
  ///
  /// GET KEEP ME LOGGED IN STATE FROM REDUX STORE
  interface RootStateReducerKeepMeLoggedIn {
    GlobalReducerKeepMeLoggedIn: {
      rememberMe: boolean;
    };
  }
  const { rememberMe } = useSelector(
    (state: RootStateReducerKeepMeLoggedIn) => ({
      ...state.GlobalReducerKeepMeLoggedIn,
    })
  );

  const rememberMeReducer = rememberMe;
  var opacityremember = "";
  var rememberMeType = "";
  var FeedBackData = "";

  if (rememberMeReducer) {
    opacityremember = "1";
    rememberMeType = "session";
    FeedBackData = "user will stay logged in on browser close";
  } else {
    opacityremember = "0.5";
    rememberMeType = "forever";
    FeedBackData = "user will be forgotten on browser close";
  }

  var marginData: CSS.Properties;
  var fontData: CSS.Properties;

  const fontDataPc: CSS.Properties = { fontSize: "2.4vw" };
  const fontDataTablet: CSS.Properties = { fontSize: "3.9vh" };
  const fontDataMobile: CSS.Properties = { fontSize: "4.1vh" };

  const marginDataPc: CSS.Properties = {
    marginLeft: "-35px",
    opacity: opacityremember,
  };

  const marginDataMobile: CSS.Properties = {
    marginLeft: "-15.5px",
    marginTop: "5px",
    opacity: opacityremember,
  };

  var paddingttop = "";

  ///
  ///
  ///
  ///CONDITIONAL STATEMENT FOR DEVICE TYPE
  if (matchPc) {
    marginData = marginDataPc;
    fontData = fontDataPc;
    paddingttop = "2.1rem";
    ///
  } else if (matchTablet) {
    marginData = marginDataPc;
    fontData = fontDataTablet;
    paddingttop = "5.5rem";
    ///
  } else {
    marginData = marginDataMobile;
    fontData = fontDataMobile;
    paddingttop = "2.1rem";
  }

  //
  //
  //
  //USEREF TARGETS A DIV(BACKGROUND) AND CLOSES MODAL ON CLICK
  const inputRef = useRef<HTMLInputElement>(null);
  const onBackgroundFocus = (e: any): void => {
    if (inputRef.current === e.target) {
      closemodal(0);
    }
  };

  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animation = useSpring({
    config: {
      duration: 250,
    },
    opacity: showModal ? 1 : 0.0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`,
  });

  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  const rememberTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const rememberUser = () => {
    dispatch(rememberMeAction());
    setShowModal(false);
    setFeedbackshow(true);
    if (rememberTimer.current) {
      clearTimeout(rememberTimer.current);
    }
    rememberTimer.current = setTimeout(() => {
      setFeedbackshow(false);
    }, 3000);
    Axios.post(
      `http://${REACT_APP_SUPERSTARZ_URL}/keepmeloggedin`,
      {
        values: rememberMeType,
      },
      {
        withCredentials: true,
      }
    )
      .then((response) => {
        if (response.data.message === "session_Cookie_Activated") {
        } else if (response.data.message === "forever_Cookie_Activated") {
        }
      })
      .catch(function (error) {
        alert("Connection failure ");
      });
  };

  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
    };
  }
  const { color } = useSelector((state: RootStateReducerColor) => ({
    ...state.GlobalReducerColor,
  }));

  ///
  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );
  var PaperStyleReducer = "";
  ///
  ///
  ///
  ///CONDITIONAL STATEMENT FOR DARKMODE
  if (darkmodeReducer) {
    PaperStyleReducer = PaperStyleDark;
  } else {
    PaperStyleReducer = PaperStyleLight;
  }

  const colorReducer = color;
  const jj = "#00ccff";

  const defaultMaterialTheme = makeStyles((theme) => ({
    picker: {
      backgroundColor: "#00ccff",
    },
  }));

  let themeGeneralSettings = createMuiTheme({
    palette: {
      type: darkmodeReducer ? "dark" : "light",
    },
  });

  var useStyle = makeStyles((themeGeneralSettings) => ({
    optionsContainer: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "3vh",
        top: "1em",
        fontWeight: "bold",
        display: "flex",
        alignItems: "center",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        padding: "4.5vh",
        top: "1em",
        fontWeight: "bold",
        display: "flex",
        alignItems: "center",
      },
    },
    PaperStyle: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        backgroundImage: darkmode
          ? "linear-gradient(0deg, #282c34, #282c36, #191919 )"
          : "linear-gradient(0deg, rgb(220,225,225), rgb(235,237,239),rgb(246,250,255) )",
      },
    },
  }));

  var classes = useStyle();

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper style={{ borderRadius: "0px" }}>
        <Grid container style={{}}>
          {showModal ? (
            <DialogContent
              className="dialog-container dontallowhighlighting "
              onClick={onBackgroundFocus}
              ref={inputRef}
              style={{
                padding: "0px",
                height: "100vh",
                position: "fixed",
              }}
            >
              <animated.div style={animation}>
                <Grid
                  container
                  style={{ paddingTop: paddingttop }}
                  className={
                    darkmodeReducer
                      ? "Background-header-dark theme-more-container"
                      : "Background-header-light theme-more-container"
                  }
                >
                  <Grid item sm={8} md={10} xs={7}>
                    <OverrideMaterialUICss>
                      <ThemeProvider theme={defaultMaterialTheme}>
                        <MuiPickersUtilsProvider utils={DateFnsUtils}>
                          <DatePicker
                            disableFuture
                            openTo="year"
                            format="dd/MM/yyyy"
                            label="Date of birth"
                            views={["year", "month", "date"]}
                            value={selectedDate}
                            onChange={handleDateChange}
                            inputProps={{ style: { fontSize: "3.9em" } }}
                            InputLabelProps={{ style: { fontSize: "1.6em" } }}
                          />
                        </MuiPickersUtilsProvider>
                      </ThemeProvider>
                    </OverrideMaterialUICss>
                  </Grid>
                  <Grid
                    item
                    xs={2}
                    sm={2}
                    md={1}
                    className="zuperking"
                    style={marginData}
                  >
                    <RestoreIcon
                      onClick={rememberUser}
                      titleAccess={
                        rememberMeReducer
                          ? "Forget User On Browser Close"
                          : "Keep Me Logged In"
                      }
                      className={
                        darkmodeReducer
                          ? "make-small-icons-clickable-dark dontallowhighlighting  "
                          : "make-small-icons-clickable-light  dontallowhighlighting  "
                      }
                      style={fontData}
                    />
                  </Grid>

                  <Grid
                    item
                    xs={3}
                    className="dontallowhighlighting add-rounded-icon-right"
                    sm={2}
                    md={1}
                  >
                    <CakeIcon
                      titleAccess="Birthday"
                      className={
                        darkmodeReducer
                          ? "make-small-icons-clickable-dark  dontallowhighlighting  "
                          : "make-small-icons-clickable-light  dontallowhighlighting  "
                      }
                      style={fontData}
                    />
                  </Grid>
                </Grid>
              </animated.div>
            </DialogContent>
          ) : (
            <SuperCheckFeedBack
              Feedbackshow={Feedbackshow}
              rememberTimer={rememberTimer}
              setFeedbackshow={setFeedbackshow}
              FeedBackData={FeedBackData}
              rememberMeType={rememberMeType}
              setShowModal={setShowModal}
            />
            /*  <SuperBirthdayFeedBack
          Feedbackshow={Feedbackshow}
          setFeedbackshow={setFeedbackshow}
          FeedBackData={FeedBackData}
            setFeedBackData={setFeedBackData}
        
        />*/
          )}
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export const SupercheckOptions = React.memo(SupercheckOptionsx);
