  const canvasRef: any = useRef(null);

  const canvasRefdummy: any = useRef(null);

  const closegate = () => {
    if (index === 0) {
      ////
      ///addfilter();
    } else {
      setActivatefilterImage(false);
    }
  };

  function blend(
    background: any,
    foreground: any,
    width: any,
    height: any,
    transform: any
  ) {
    // Side note: grabbing this data is the most expensive piece. For better
    // performance, you could consider caching this data
    var bottom = background.getImageData(0, 0, width, height);
    var top = foreground.getImageData(0, 0, width, height);

    for (var i = 0, size = top.data.length; i < size; i += 4) {
      // red
      top.data[i + 0] = transform(bottom.data[i + 0], bottom.data[i + 0]);
      // green
      top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
      // blue
      top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
      // the fourth slot is alpha. We don't need that (so skip by 4)
    }

    return top;
  }

  function toasterGradientJentle(width: any, height: any) {
    ////////////gradientcolor
    var ctx = canvasRefdummy.current.getContext("2d");

    canvasRefdummy.current.height = height;
    canvasRefdummy.current.width = width;

    // Fill a Radial Gradient
    // https://developer.mozilla.org/en-US/docs/Web/API/CanvasRenderingContext2D/createRadialGradient

    var gradient = ctx.createRadialGradient(
      width / 2,
      height / 2,
      0,
      width / 2,
      height / 2,
      width * 0.4
    );

    gradient.addColorStop(0, "rgba(145,70,101,1)");
    gradient.addColorStop(1, "rgba(67, 43, 92,1)");

    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, width, height);

    ////////////gradientcolor
    return ctx;
  }

  const ApplyImageFilter = (type: string) => {
    //////////////////INITIALIZE  CANVAS
    const previewFileReadimage = new Image();
    previewFileReadimage.src = itemUploadRef.current[index].src;
    previewFileReadimage.onload = function () {
      const ctx = canvasRef.current.getContext("2d");
      var widthx = window.innerWidth / 2.5;
      var scalex = widthx / previewFileReadimage.naturalWidth;
      var heightx = previewFileReadimage.naturalHeight * scalex;
      var width = window.innerWidth / 0.5;
      var scale = width / previewFileReadimage.naturalWidth;
      var height = previewFileReadimage.naturalHeight * scale;
      canvasRef.current.height = height;
      canvasRef.current.width = width;
      //////////////////INITIALIZE  CANVAS

      requestAnimationFrame(() => {
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER
        if (type === "lift") {
          ctx.filter = "contrast(1.3) brightness(1) blur(0px)";
        }
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER
        ctx.drawImage(
          previewFileReadimage,
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        ////////////////// /////// GLOBALCOMPOSITEOPERATION
        if (type === "lift") {
          ////////////composition
          ctx.globalCompositeOperation = "screen";
          ctx.fillStyle = "rgba(100, 100, 100,0.07)";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        }
        ////////////////// /////// GLOBALCOMPOSITEOPERATION
      });

      canvasRef.current.style.width = `${widthx}px`;
      canvasRef.current.style.height = `${heightx}px`;
    };

    ////////////////////
    ////
  };

  const jentle = () => {
    const previewFileReadimage = new Image();
    previewFileReadimage.src = itemUploadRef.current[index].src;
    previewFileReadimage.onload = function () {
      const ctx = canvasRef.current.getContext("2d");

      var widthx = window.innerWidth / 2.5;
      var scalex = widthx / previewFileReadimage.naturalWidth;
      var heightx = previewFileReadimage.naturalHeight * scalex;

      var width = window.innerWidth / 0.5;
      var scale = width / previewFileReadimage.naturalWidth;
      var height = previewFileReadimage.naturalHeight * scale;

      canvasRef.current.height = height;
      canvasRef.current.width = width;

      ////////////////////

      requestAnimationFrame(() => {
        ctx.filter = "contrast(1.15) brightness(1) hue-rotate(20deg) ";

        ctx.drawImage(
          previewFileReadimage,
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        ////////////composition

        ////////////composition

        ctx.globalCompositeOperation = "soft-light";
        ctx.fillStyle = "rgba(97, 79, 77,1)";
        ctx.fillRect(0, 0, width, height);
        ////////////composition

        ///var gradient = toasterGradientJentle(width, height);

        var screen = blend(
          ctx,
          gradient,
          width,
          height,
          function (bottomPixel: any, topPixel: any) {
            return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
          }
        );

        // replace `ctx.drawImage(gradient.canvas, 0, 0)` with this:
        ctx.putImageData(screen, 0, 0);
      });
      canvasRef.current.style.width = `${widthx}px`;
      canvasRef.current.style.height = `${heightx}px`;
    };

    ////////////////////
    ////
  };

  const Floyd = () => {
    const previewFileReadimage = new Image();
    previewFileReadimage.src = itemUploadRef.current[index].src;
    previewFileReadimage.onload = function () {
