      canvasRef.current.width = width;

      ////////////////////

      requestAnimationFrame(() => {
        ctx.filter = "contrast(1.2) brightness(0.85) saturate(0.81)";

        ctx.drawImage(
          previewFileReadimage,
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        ////////////composition
        ctx.globalCompositeOperation = "xor";
        ctx.fillStyle = "#07070a17";
        ctx.fillRect(0, 0, width, height);
        ////////////composition

        ///var gradient = toasterGradientNebula(width, height);

        var screen = blend(
          ctx,
          gradient,
          width,
          height,
          function (bottomPixel: any, topPixel: any) {
            return 255 - ((252 - topPixel) * (250 - bottomPixel)) / 255;
          }
        );

        // replace `ctx.drawImage(gradient.canvas, 0, 0)` with this:
        ctx.putImageData(screen, 0, 0);

        const scannedImage = ctx.getImageData(
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        const scannedData: any = scannedImage.data;

        ///

        for (let i = 0; i < scannedData.length; i += 4) {
          scannedData[i] = scannedData[i] / 2 + scannedData[i] / 3.6;
          scannedData[i + 1] = scannedData[i + 1];
          scannedData[i + 2] = scannedData[i + 2];
        }
        ///
        ctx.putImageData(scannedImage, 0, 0);
      });

      canvasRef.current.style.width = `${widthx}px`;
      canvasRef.current.style.height = `${heightx}px`;
    };
  };

  return (
    <>
      {itemUploadRef.current[index] ? (
        <>
          {" "}
          <button
            onClick={() => {
              ApplyImageFilter("lift");
            }}
          >
            lit
          </button>
          <button onClick={Floyd}> floyd </button>
          <button onClick={jentle}> jentle </button>
          <button onClick={mint}> mint </button>
          <button onClick={nebula}> nebula </button>
          <img
            id={itemUploadRef.current[index]}
            ref={filterbaseimageRef}
            onClick={closegate}
            src={
              itemUploadRef.current ? itemUploadRef.current[index].src : null
            }
            style={{
              width: "30%",
              height: "auto",
              position: "relative",
              margin: "auto",
              marginRight: index === 0 ? "20px" : "0px",
            }}
          />
          <canvas
            ref={canvasRef}
            style={{
              padding: "0px",
            }}
          />
          <canvas
            ref={canvasRefdummy}
            style={{
              padding: "0px",
              display: "none",
            }}
          />
        </>
      ) : null}
    </>
  );
}

