import "./App.css";
import "typeface-roboto";
import React, { useState } from "react";
import imyz from "./images/s.png";
import imyzd from "./images/sd.png";
import { PcLogin, MobileLogin } from "./appfolder/Login";
import {
  useTheme,
  Paper,
  useMediaQuery,
  Grid,
  makeStyles,
  createMuiTheme,
  responsiveFontSizes,
  Typography,
  MuiThemeProvider,
} from "@material-ui/core";
import Option from "./appfolder/Option";
import * as CSS from "csstype";

function App(): JSX.Element {
  var usetheme = useTheme();
  var match = useMediaQuery(usetheme.breakpoints.up("sm"));
  const [darkmode, setDarkmode] = useState<boolean>(true);

  interface IappVariables {
    shade: string;
    shade2: string;
    shade2num: string;
    shade2nump: string;
    logoimage: string;
    secondarymaincolor: string;
    maincolor: string;
    shade2nump22: string;
  }

  var appVariables: IappVariables = {
    shade: "",
    shade2: "",
    shade2num: "",
    shade2nump: "",
    logoimage: "",
    secondarymaincolor: "",
    maincolor: "",
    shade2nump22: "",
  };

  if (darkmode) {
    var appVariables: IappVariables = {
      shade: "#cccccc",
      shade2: "#cccccc",
      shade2num: "1.1",
      shade2nump: "1.8",
      logoimage: imyzd,
      secondarymaincolor: "#ffffff",
      maincolor: "#ffffff",
      shade2nump22: "5.5",
    };
  } else {
    var appVariables: IappVariables = {
      shade: "#0b1728",
      shade2: "#0b1728",
      shade2num: "1.5",
      shade2nump: "1.5",
      logoimage: imyz,
      secondarymaincolor: "#0b1728",
      maincolor: "#0b1728",
      shade2nump22: "8",
    };
  }

  let themeGeneralSettings = createMuiTheme({
    palette: {
      background: {
        paper: darkmode ? "#282c34" : "#ffffff",
      },
      primary: {
        main: `${appVariables.maincolor}`,
      },
      secondary: {
        main: `${appVariables.secondarymaincolor}`,
      },
      type: darkmode ? "dark" : "light",
    },
  });

  var useStyle = makeStyles((themeGeneralSettings) => ({
    icon: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        objectFit: "contain",
        width: "78%",
        paddingLeft: "1.5vw",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        objectFit: "contain",
        width: "85%",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        objectFit: "contain",
        width: "60%",
      },
    },

    h1: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        fontFamily: "BROADW",
        fontSize: "9.8vw",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        fontFamily: "BROADW",
        fontSize: "8vw",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        fontFamily: "BROADW",
        fontSize: "5.8vw",
      },
    },

    loginclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.5px",
        borderRadius: "20px",
        fontSize: "13px",
        mozBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        webkitBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        boxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
      },
    },

    signupclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "10.5px",
        borderRadius: "20px",
        fontSize: "13px",
        mozBoxShadow: `0 0 4.5px ${appVariables.shade}`,
        webkitBoxShadow: `0 0 4.5px ${appVariables.shade} `,
        boxShadow: `0 0 4.5px ${appVariables.shade}`,
      },
    },
    loginclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        boxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
      },
    },
    signupclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        boxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
      },
    },
  }));
  var classes = useStyle();

  const fontDataPc: CSS.Properties = { fontSize: "2.2rem" };

  const fontDataMobile: CSS.Properties = {
    fontSize: "1.5rem",
  };

  const marginDataPc: CSS.Properties = {
    marginLeft: "-20px",
  };

  const marginDataMobile: CSS.Properties = {
    marginLeft: "-5.5px",
  };

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper className="fadeboyin app" style={{ borderRadius: "0px" }}>
        {match ? (
          <Option
            switchsize="medium"
            darkmode={darkmode}
            setDarkmode={setDarkmode}
            marginData={marginDataPc}
            fontData={fontDataPc}
          />
        ) : (
          <Option
            switchsize="small"
            darkmode={darkmode}
            setDarkmode={setDarkmode}
            marginData={marginDataMobile}
            fontData={fontDataMobile}
          />
        )}

        <Grid
          container
          className={match ? "containerapp" : "containerappmobile"}
        >
          <Grid item sm={1} md={2}></Grid>
          <Grid
            item
            className="centericon   dontallowselect"
            xs={3}
            sm={2}
            md={2}
          >
            <img
              className={classes.icon}
              src={appVariables.logoimage}
              alt="SuperstarZ logo"
            />
          </Grid>
          <Grid item xs={9} sm={8} md={6}>
            <Typography variant="h1" className={classes.h1}>
              <p className={darkmode ? "texticondark" : "texticonlight"}>
                SUPERSTARZ
              </p>
            </Typography>
          </Grid>
          {match ? (
            <PcLogin
              loginclass={classes.loginclasspc}
              signupclass={classes.signupclasspc}
            />
          ) : (
            <MobileLogin
              loginclass={classes.loginclassmobile}
              signupclass={classes.signupclassmobile}
            />
          )}
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export default App;
