import React, { useRef, useState, useEffect } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import Cropper from "react-easy-crop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Slider from "@mui/material/Slider";
import { AnySoaRecord } from "dns";
import { PreviewCanvas } from "./PreviewCanvas";

function SuperCropx({ cropimage, selectedImage }: any): JSX.Element {
  const cropCanvasRef: any = useRef(null);

  ////
  ////
  ////
  const [cropArea, setcropArea] = useState<any>(null);
  const [crop, setcrop] = useState<any>({ x: 0, y: 0 });
  const [zoom, setzoom] = useState(1);

  const [cropwidth, setcropwidth] = useState(0);
  const [cropheight, setcropheight] = useState(0);

  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const getpreviewFixedWidthRef = useRef<HTMLDivElement>(null);

  const getCropHeightRef: any = useRef<HTMLDivElement>(null);

  const getCropHeightFullscreenRef: any = useRef<HTMLDivElement>(null);

  const getFixedCropWidthRef: any = useRef<HTMLDivElement>(null);

  const [getCropHeight, setgetCropHeight] = useState(0);

  const [getCropHeightFullscreen, setgetCropHeightFullscreen] = useState(0);

  const [getFixedCropWidth, setgetFixedCropWidth] = useState(0);

  const [getpreviewFixedWidth, setgetpreviewFixedWidth] = useState(0);

  const [CropImageHolder, setCropImageHolder] = useState<any>(null);

  const [OriginalImageWidth, setOriginalImageWidth] = useState<number>(0);
  const [OriginalImageHeight, setOriginalImageHeight] = useState<number>(0);

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (
      getpreviewFixedWidthRef.current &&
      getpreviewFixedWidthRef.current.clientWidth
    ) {
      setgetpreviewFixedWidth(getpreviewFixedWidthRef.current.clientWidth);
    }

    if (getCropHeightRef.current && getCropHeightRef.current.clientWidth) {
      setgetCropHeight(getCropHeightRef.current.clientHeight);
    }
    if (
      getCropHeightFullscreenRef.current &&
      getCropHeightFullscreenRef.current.clientWidth
    ) {
      setgetCropHeightFullscreen(
        getCropHeightFullscreenRef.current.clientHeight
      );
    }

    if (
      getFixedCropWidthRef.current &&
      getFixedCropWidthRef.current.clientWidth
    ) {
      setgetFixedCropWidth(getFixedCropWidthRef.current.clientWidth);
    }
  }, []);
  ///

  useEffect(() => {
    const Newcropimage = new Image();
    Newcropimage.src = cropimage;
    Newcropimage.onload = function () {
      setCropImageHolder(Newcropimage);

      var data1 = Newcropimage.naturalWidth * 2;
      var data2 = Newcropimage.naturalHeight * 2;
      setOriginalImageWidth(data1);
      setOriginalImageHeight(data2);

      setcropwidth(data1);
      setcropheight(data2);
    };
  }, [cropimage]);

  useEffect(() => {
    if (CropImageHolder && cropCanvasRef.current) {
      const ctx = cropCanvasRef.current.getContext("2d");

      var RatiofromOriginalandfixedHeight = OriginalImageHeight / getCropHeight;
      var NewBoxRatioWidth_WIDEIMAGE =
        OriginalImageWidth / RatiofromOriginalandfixedHeight;

      var RatiofromOriginalandfixedHeight =
        OriginalImageHeight / getCropHeightFullscreen;
      var NewBoxRatioWidth_LONGIMAGE =
        OriginalImageWidth / RatiofromOriginalandfixedHeight;

      cropCanvasRef.current.width = getpreviewFixedWidth * zoom;
      cropCanvasRef.current.height = getCropHeight * zoom;

      if (OriginalImageWidth > OriginalImageHeight) {
        ctx.drawImage(
          CropImageHolder,
          crop.x,
          crop.y,
          NewBoxRatioWidth_WIDEIMAGE * zoom,
          getCropHeight * zoom
        );
      } else {
        ctx.drawImage(
          CropImageHolder,
          crop.x,
          crop.y,
          NewBoxRatioWidth_LONGIMAGE * zoom,
          getCropHeightFullscreen * zoom
        );
      }

      cropCanvasRef.current.style.width = `${getpreviewFixedWidth}px`;
      cropCanvasRef.current.style.height = `${getCropHeight}px`;
    }
  }, [CropImageHolder, cropCanvasRef, OriginalImageWidth, OriginalImageHeight]);

  return (
    <>
      <Grid
        container
        ref={getCropHeightRef}
        style={{
          position: "relative",
          width: "100%",
          height: "70%",
        }}
      >
        <Grid item xs={12}>
          <Grid
            item
            ref={getCropHeightFullscreenRef}
            xs={1}
            style={{ height: "100vh", visibility: "none" }}
          ></Grid>
          <Grid item ref={getFixedCropWidthRef} xs={4}></Grid>
        </Grid>

        <canvas
          className={
            darkmodeReducer ? "turlightpostdarkx" : "turlightpostlightx"
          }
          ref={cropCanvasRef}
          key={cropimage}
          style={{
            padding: "0px",
            backgroundColor: "#00ccff",
            margin: "auto",
          }}
        />
      </Grid>

      <Grid
        item
        xs={12}
        style={{
          padding: "0px",
          backgroundColor: "#00ccff",
        }}
      >
        <Grid
          item
          ref={getpreviewFixedWidthRef}
          xs={4}
          style={{
            padding: "0px",
          }}
        ></Grid>
      </Grid>
      <Masonry
        columns={matchPc ? 3 : 1}
        spacing={0}
        style={{ overflowX: "hidden" }}
      >
        {selectedImage
          ? selectedImage.map((photo: any, index: any) => {
              return (
                <div key={index}>
                  <PreviewCanvas
                    cropheight={cropheight}
                    cropwidth={cropwidth}
                    CropImageHolder={CropImageHolder}
                    cropCanvasRef={cropCanvasRef}
                    crop={crop}
                    zoom={zoom}
                    index={index}
                    image={photo}
                    getpreviewFixedWidth={getpreviewFixedWidth}
                  />
                </div>
              );
            })
          : null}{" "}
      </Masonry>
    </>
  );
}

export const SuperCrop = React.memo(SuperCropx);
