import React, { useState, useRef } from 'react';
import { createFFmpeg } from '@ffmpeg/ffmpeg';
import { fetchFile, toBlobURL } from '@ffmpeg/util';

const VideoFFmpegx = ({ VideoUrl }: any) => {
    const [recordedVideoUrl, setRecordedVideoUrl] = useState(null);
    const ffmpegRef = useRef(null);

    if (!ffmpegRef.current) {
        ffmpegRef.current = createFFmpeg({ log: true });
    }

    const loadFFmpeg = async () => {
        const ffmpeg: any = ffmpegRef.current;
        const baseURL = 'https://unpkg.com/@ffmpeg/core-mt@0.12/dist/umd/';

        try {
            if (ffmpeg) {



                ffmpeg.load({
                    coreURL: await toBlobURL(`${baseURL}/ffmpeg-core.js`, 'text/javascript'),
                    wasmURL: await toBlobURL(`${baseURL}/ffmpeg-core.wasm`, 'application/wasm'),
                    workerURL: await toBlobURL(`${baseURL}/ffmpeg-core.worker.js`, 'text/javascript')

                });



            }
        } catch (error: any) {
            // Handle or log the error
            console.error('Error loading FFmpeg:', error);

            // Specific handling for SharedArrayBuffer error
            if (error.message.includes('SharedArrayBuffer')) {
                console.error('SharedArrayBuffer is nt defined. Check if your site is correctly set up for cross-origin isolation.');
                // Implement fallback or additional error handling
            }
        }
    };



    const processVideo = async (sourceVideoUrl: string) => {
        const ffmpeg: any = ffmpegRef.current;

        try {
            if (!ffmpeg.isLoaded()) {
                await loadFFmpeg();
                alert('FFmpeg loaded successfully.');
            }




        } catch (error) {
            console.error('Error procesing video:', error);
        }
    };


    return (
        <div>
            <button onClick={() => processVideo(VideoUrl)}>Process Video</button>
            {recordedVideoUrl && <video src={recordedVideoUrl} controls style={{ width: '100%' }}></video>}
        </div>
    );
};

export const VideoFFmpeg = React.memo(VideoFFmpegx);
