import React, { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { ImodalFormSignupError } from "./appFolder-Interfaces";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import LinearProgress from "@material-ui/core/LinearProgress";

export default function ModalLogFormError({
  textField,
  errorFormChecking,
  type,
  cantPassBadEmail,
  ErrorDisplay,
  ErrorData,
  darkmode,
  WidthHolder,
}: ImodalFormSignupError) {
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalError = useSpring({
    config: {
      duration: 100,
    },

    opacity: ErrorDisplay,
    transform: ErrorData ? `translateY(0%)` : `translateY(100%)`,
  });

  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalErrorPassword = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    marginTop: "-64px",
    zIndex: 0,
    transform: ErrorData ? `translateY(100%)` : `translateY(0%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#22222";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "turlight";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  // Inspired by the former Facebook spinners.
  const useStylesFacebook = makeStyles((theme) => ({
    root: {
      position: "relative",
      textAlign: "left",
      left: 10,
    },
    bottom: {
      color: theme.palette.grey[theme.palette.type === "light" ? 300 : 700],
    },
    top: {
      color: ErrorIconColor,
      animationDuration: "550ms",
      position: "absolute",
      left: 0,
    },
    circle: {
      strokeLinecap: "round",
    },
  }));

  const classes = useStylesFacebook();

  let ShowSpinnerOrData = `  `;
  let ShowSpinnerOrData2 = ` `;
  let ShowSpin = ` 
 `;

  if (ErrorData === "Checking") {
    ShowSpinnerOrData = ShowSpin;
    ShowSpinnerOrData2 = ShowSpin;
  }

  let temi = `  <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />`;
  return (
    <>
      {type ? (
        <animated.div style={animationModalError}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "-1.5px",
              display: "flex",
            }}
          >
            <Grid
              className={errorFormChecking ? "display-off" : "display-on"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",

                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>{temi}</Grid>
              <Grid style={{ opacity: 0.7 }} xs={10}>
                {ErrorData}
              </Grid>
            </Grid>
            <Grid
              className={errorFormChecking ? "display-on" : "display-off"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",

                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-start",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              {" "}
              <div className={classes.root}>
                <CircularProgress
                  variant="determinate"
                  className={classes.bottom}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                  value={100}
                />
                <CircularProgress
                  variant="indeterminate"
                  disableShrink
                  className={classes.top}
                  classes={{
                    circle: classes.circle,
                  }}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                />
              </div>{" "}
            </Grid>
          </Grid>
        </animated.div>
      ) : (
        <animated.div style={animationModalErrorPassword}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",

              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",

              display: "flex",
            }}
          >
            {" "}
            <Grid
              className={errorFormChecking ? "display-off" : "display-on"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",

                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid xs={10} style={{ opacity: 0.6 }}>
                {ErrorData}
              </Grid>
            </Grid>
            <Grid
              className={errorFormChecking ? "display-on" : "display-off"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",

                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-start",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              {" "}
              <div className={classes.root}>
                <CircularProgress
                  variant="determinate"
                  className={classes.bottom}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                  value={100}
                />
                <CircularProgress
                  variant="indeterminate"
                  disableShrink
                  className={classes.top}
                  classes={{
                    circle: classes.circle,
                  }}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                />
              </div>{" "}
            </Grid>
          </Grid>
        </animated.div>
      )}
    </>
  );
}
