import React, { useRef, useEffect, useCallback, useState } from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";

function Uploadx({
  showModalUpload,
  closeUploadModal,
  OpenUploadModal,
  getSliderWidth,
}: any): JSX.Element {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animation = useSpring({
    config: {
      duration: 600,
    },
    opacity: showModalUpload ? 1 : 0,
    transform: showModalUpload ? `translateY(0%)` : `translateY(-100%)`,
  });

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const blank = () => {};

  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  const [image, setimage] = useState<any>(null);
  const [imageWidth, setimageWidth] = useState<any>(null);
  const [imageHeight, setimageHeight] = useState<any>(null);

  const canvasRef: any = useRef(null);

  useEffect(() => {
    if (image && canvasRef.current) {
      const catimage = new Image();
      catimage.src = "./z.jpg";
      catimage.onload = function () {
        setimage(catimage);
        setimageWidth(catimage.naturalWidth);
        setimageHeight(catimage.naturalHeight);
      };
      var sourceX = 150;
      var sourceY = 0;
      var sourceW = imageWidth;
      var sourceH = imageHeight;
      var destW = sourceW;
      var destH = sourceH;
      var destX = canvasRef.width / 2 - destW / 2;
      var destY = canvasRef.height / 2 - destH / 2;
      const ctx = canvasRef.current.getContext("2d");

      ctx.drawImage(
        image,
        200,
        100,
        imageWidth,
        imageHeight,
        100,
        0,
        imageWidth,
        imageHeight
      );
    }
  }, [image, canvasRef, imageWidth, imageHeight]);

  ////canvas control size of both
  //// fill react  control size of black inner rectangle
  ////drawimage  control size of actual image
  /***** kkkk 
   *   ctx.fillRect(0, 0, imageWidth + 80, imageHeight + 80);
      ctx.drawImage(image, (imageWidth - imageWidth) / 2 + 40, 40);
 <canvas
                  ref={canvasRef}
                  width={`${imageWidth + 80}`}
                  height={`${imageHeight + 80}`}
                />

                for image with symetrixc black box

    */

  return (
    <>
      {showModalUpload ? (
        <DialogContent
          className={
            darkmodeReducer ? "dialog-container  " : "dialog-container  "
          }
          style={{
            padding: "0px",
            height: "100vh",
            position: "fixed",
            zIndex: 100,
          }}
        >
          <animated.div style={animation}>
            <DialogContent
              className={
                darkmodeReducer
                  ? " dontallowhighlighting modal-containerDark "
                  : " dontallowhighlighting  modal-containerLight "
              }
              style={{
                padding: "0px",
                height: "100vh",
              }}
            >
              <div>
                <canvas
                  ref={canvasRef}
                  width={`${imageWidth + 80}`}
                  height={`${imageHeight + 80}`}
                />
              </div>
            </DialogContent>
          </animated.div>
        </DialogContent>
      ) : null}
    </>
  );
}

export const Upload = React.memo(Uploadx);
