import React, { useState, useCallback, useEffect } from "react";

import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
/// USE DISPATCH
import { useSelector, useDispatch } from "react-redux";
import { usePalette } from "react-palette";

import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";

function AboutColorx(): JSX.Element {
  const dispatch = useDispatch();
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
    };
  }
  const { color, colordark } = useSelector((state: RootStateReducerColor) => ({
    ...state.GlobalReducerColor,
  }));
  const colorReducer = color;
  const colorReducerdark = colordark;

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
    };
  }
  const { image } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const imageReducer = image;

  ///
  ///
  ///
  /// REACT PALLETTE (VIBRANTCOLOR DETECTION)
  ////  const { data, loading, error } = usePalette(`./images/profile/${imageReducer}`);
  const { data } = usePalette(`./images/profile/${imageReducer}`);

  console.log(data);

  return (
    <>
      <Grid container>
        <Grid
          item
          xs={12}
          style={{ marginTop: "3vh", padding: "0px", marginLeft: "2.3vw" }}
        >
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? colorReducerdark
                : colorReducer,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "0%",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            style={{
              textAlign: "center",
              alignItems: "center",
              display: "grid",
              justifyContent: "center",
              margin: "auto",
              width: `8vh`,
              height: `8vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? "rgba(010,010,010, 0.7)"
                : "rgba(220,220,220, 0.7)",
              borderRadius: "50%",
              zIndex: 1,
              marginLeft: "0.25vw",
              marginTop: "0.5vh",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: data.vibrant,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "7%",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? data.darkVibrant
                : data.lightVibrant,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "14%",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: data.muted,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "21%",
            }}
          >
            {" "}
          </Grid>{" "}
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? data.darkMuted
                : data.lightMuted,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "28%",
            }}
          >
            {" "}
          </Grid>
        </Grid>{" "}
      </Grid>
    </>
  );
}

export const AboutColor = React.memo(AboutColorx);
