      <Grid
        container
        style={{ height: "100%", position: "fixed", top: "-800vh" }}
      >
        <Grid
          item
          xs={12}
          style={{
            padding: "0px",
          }}
        >
          <Grid
            item
            ref={getScreenWidthRef}
            xs={12}
            style={{ padding: "0px", width: "100%" }}
          ></Grid>
          <Grid
            item
            ref={getCropHeightRef}
            xs={5}
            style={{ padding: "0px", height: matchMobile ? "72%" : "78%" }}
          ></Grid>

          <Grid
            item
            ref={getCropHeightRefmobile}
            xs={11}
            style={{ padding: "0px", height: "78%" }}
          ></Grid>

          <Grid
            item
            ref={getCropHeightRef2}
            xs={5}
            style={{ padding: "0px", height: matchMobile ? "23%" : "22%" }}
          ></Grid>
        </Grid>
      </Grid>



      {ConfirmUpload ?
        <Grid
          container
          style={{
            height: "100%", position: "fixed", top: "2vh", padding: "0px",
            zIndex: 200,
            backgroundColor: darkmodeReducer ? 'rgb(000,000,000,0.35)' : 'rgb(255,255,255,0.35)'
          }}
        ><Grid
          item
          xs={12}
          style={{
            padding: '0px'
          }}
        ></Grid>

          <Grid
            item
            xs={4}
            style={{
              padding: '0px'
            }}
          ></Grid>
          <Grid
            item
            xs={4}
            style={{
              padding: '0px',
              backgroundColor: 'red',
              marginTop: '-20px'
            }}
          ></Grid>
          <Grid
            item
            xs={4}
            style={{
              padding: '0px'
            }}
          ></Grid>

        </Grid> : null
      }




    </>
  );
}

export const FilterMode = React.memo(FilterModex);
