import React from "react";
import {
  IconButton,
  InputAdornment,
  Grid,
  DialogContent,
  Paper,
  makeStyles,
  TextField,
  Button,
} from "@material-ui/core";
import { IDialogeDataMobile } from "./appFolder-Interfaces";
import { useSpring, animated } from "react-spring";
import ModalFormSignupError from "./ModalFormSignupError";
import ModalFormLoginError from "./ModalFormLoginError";
import ServerError from "./ServerError";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";

function MobileModalLog(DialogeDataMobile: IDialogeDataMobile) {
  ///
  ///
  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 1000,
    },
    opacity: DialogeDataMobile.showModalForm ? 1 : 0.9,
    transform: DialogeDataMobile.showModalForm
      ? `translateY(0%)`
      : `translateY(100%)`,
  });

  const modalanimationTwo = useSpring({
    config: {
      duration: 900,
    },
    opacity: DialogeDataMobile.showModalForm ? 1 : 0.9,
    transform: DialogeDataMobile.showModalForm
      ? `translateY(0%)`
      : `translateY(100%)`,
  });

  ///
  ///
  ///
  /// MATERIAL UI  CUSTOM STYLES
  var useStyleModal = makeStyles(() => ({
    containerStyle: {
      width: DialogeDataMobile.imageReal,
      height: "auto",
      boxShadow: "0 5px 16px rgba(0, 0, 0, 0.2)",
      color: "#000000",
      position: "relative",
      zIndex: 10,
      borderRadius: DialogeDataMobile.borderGrid,
    },
    imageStyle: {
      opacity: DialogeDataMobile.opacityFixedModalImage,
      cursor: "pointer",
      objectFit: "contain",
      width: "100%",
      height: "100%",
      borderRadius: "0px",
      padding: "0px",
    },
    imageStylex: {
      opacity: DialogeDataMobile.opacitySlidingModalImage,
      cursor: "pointer",
      width: "100%",
      height: "auto",
      borderRadius: "0px",
    },
  }));
  var classes = useStyleModal();
  return (
    <DialogContent
      className="fadermodal FormDialog-container-mobile dontallowhighlighting"
      onClick={DialogeDataMobile.onBackgroundFocus}
      style={{ overflow: "auto", cursor: "pointer", padding: "0px" }}
      ref={DialogeDataMobile.ModalBackgroundRef}
    >
      <animated.div style={modalanimation}>
        <Paper
          className={DialogeDataMobile.PaperStyle}
          style={{
            borderRadius: DialogeDataMobile.borderGrid,
            cursor: "default",
          }}
        >
          <Grid container>
            <Grid item xs={12}>
              <img
                onClick={DialogeDataMobile.zoomlogmodal}
                onLoad={DialogeDataMobile.onimageload}
                src={DialogeDataMobile.showimage}
                className={classes.imageStyle}
                alt="Logsmall"
              />
            </Grid>
            <Grid item xs={12} style={{ height: "90vh" }}>
              <Grid xs={12} item className="formholder">
                <Grid item xs={12} className="" style={{ marginTop: "0px" }}>
                  <button
                    onClick={DialogeDataMobile.logout}
                    style={{
                      cursor: "pointer",
                      opacity: 0.1,
                      position: "fixed",
                      top: "0.5em",
                      borderRadius: "50%",
                    }}
                  >
                    {" "}
                    ....
                  </button>
                  <ModalFormLoginError
                    type={true}
                    ErrorDisplay={DialogeDataMobile.errorsLoginInputedUsername}
                    darkmode={DialogeDataMobile.darkmode}
                    WidthHolder={DialogeDataMobile.WidthHolder}
                  />

                  <TextField
                    size="small"
                    style={{
                      width: "94%",
                      paddingBottom: "60px",
                    }}
                    inputProps={{ style: { fontSize: "2.5vh" } }}
                    InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                    label="Username"
                    type="text"
                    onChange={DialogeDataMobile.updateLoginvalues}
                    name="inputedUsername"
                    value={DialogeDataMobile.rawLogininputedUsername}
                    variant="filled"
                  />

                  <TextField
                    size="small"
                    style={{
                      width: "94%",
                      paddingBottom: "40px",
                      zIndex: 1,
                    }}
                    inputProps={{ style: { fontSize: "2.5vh" } }}
                    InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                    label="Password"
                    onChange={DialogeDataMobile.updateLoginvalues}
                    type={
                      DialogeDataMobile.loginShowPassword ? "text" : "password"
                    }
                    name="inputedPassword"
                    value={DialogeDataMobile.rawLogininputedPassword}
                    variant="filled"
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton
                            onClick={
                              DialogeDataMobile.ShowLoginPasswordForaWhile
                            }
                            onMouseDown={
                              DialogeDataMobile.ShowLoginPasswordForaWhile
                            }
                            aria-label="toggle password visibility"
                          >
                            {DialogeDataMobile.loginShowPassword ? (
                              <VisibilityIcon />
                            ) : (
                              <VisibilityOffIcon />
                            )}
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  />
                  <ModalFormLoginError
                    type={false}
                    ErrorDisplay={DialogeDataMobile.errorsLoginInputedPassword}
                    darkmode={DialogeDataMobile.darkmode}
                    WidthHolder={DialogeDataMobile.WidthHolder}
                  />

                  <Grid container style={{ marginTop: "30px", zIndex: 300 }}>
                    <Grid item xs={3}></Grid>
                    <Grid item className="buttonpad buttonshake" xs={6}>
                      <Button
                        onClick={DialogeDataMobile.logmein}
                        className={DialogeDataMobile.loginclass}
                        fullWidth={true}
                        variant="outlined"
                        size="large"
                        color="primary"
                      >
                        Log In
                      </Button>
                    </Grid>{" "}
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Paper>
      </animated.div>
    </DialogContent>
  );
}

export default MobileModalLog;
