import React, { useState } from "react";

import Slide from "./Slide";
import Arrow from "./Arrow";

const Slider = ({ slides }: any) => {
  const getWidth = () => window.innerWidth;
  var newGetWidth = getWidth() * slides.length;

  const [slideState, setSlideState] = useState({
    activeSlide: 0,
    translate: 0,
    transition: 0.45,
  });

  const { translate, transition, activeSlide } = slideState;

  const nextSlide = () => {
    alert("lkjn");
    setSlideState({
      ...slideState,
      activeSlide: activeSlide + 1,
      translate: (activeSlide + 1) * getWidth(),
    });
  };

  const prevSlide = () => {};

  return (
    <div
      style={{
        position: "relative",
        width: "100vw",
        height: "auto",
        margin: "0 auto",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          height: "auto",
          display: "flex",
          transform: `translateX(-${translate}px)`,
          transition: `transform ease-out(${transition}s)`,
          width: `${newGetWidth}px`,
        }}
      >
        {slides.map((slide: any) => (
          <Slide key={slide} content={slide} />
        ))}
      </div>

      <Arrow direction="left" clickSlide={prevSlide} />

      <Arrow
        direction="right"
        setSlideState={setSlideState}
        slideState={slideState}
        getWidth={getWidth}
      />
    </div>
  );
};

export default Slider;
