import React, { useRef, useState, useEffect } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import Cropper from "react-easy-crop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Slider from "@mui/material/Slider";
import { AnySoaRecord } from "dns";

function PreviewCanvasx({
  cropCanvasRef,
  image,
  getSliderWidth,
  index,
  CropImageHolder,
  zoom,
  cropheight,
  cropwidth,
}: any): JSX.Element {
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const canvasRef: any = useRef(null);

  const [imagehold, setimagehold] = useState<any>(null);
  const [imageWidth, setimageWidth] = useState<any>(null);
  const [imageHeight, setimageHeight] = useState<any>(null);

  const [imageW, setimageW] = useState<any>(null);
  const [imageH, setimageH] = useState<any>(null);

  const [imageWN, setimageWN] = useState<any>(null);
  const [imageHN, setimageHN] = useState<any>(null);

  useEffect(() => {
    const catimage = new Image();
    catimage.src = image;
    catimage.onload = function () {
      setimagehold(catimage);

      let nw = 0;
      let nh = 0;

      if (CropImageHolder) {
        nw = cropCanvasRef.current.width;
        nh = cropCanvasRef.current.height;
        setimageW(cropCanvasRef.current.width * 1.5);
        setimageH(cropCanvasRef.current.height * 1.5);
      } else {
        nw = catimage.naturalWidth;
        nh = catimage.naturalHeight;
        setimageW(catimage.naturalWidth * 1.5);
        setimageH(catimage.naturalHeight * 1.5);
      }

      let aspectH = cropwidth / cropheight;
      let h = getSliderWidth / aspectH;
      setimageHeight(h);

      let aspectHx = catimage.naturalHeight / catimage.naturalWidth;
      let hx = imageH / aspectHx;
      setimageHN(hx);
    };
  }, [
    image,
    imageHeight,
    getSliderWidth,
    cropCanvasRef,
    CropImageHolder,
    cropwidth,
    cropheight,
  ]);

  useEffect(() => {
    if (imagehold && canvasRef.current) {
      const ctx = canvasRef.current.getContext("2d");

      canvasRef.current.height = imageHeight;
      canvasRef.current.width = imageW * zoom;

      var dx = canvasRef.current.width / 2 - (imageHN * zoom) / 2;

      ctx.drawImage(imagehold, dx, 0, imageHN * zoom, imageH * zoom);

      canvasRef.current.style.width = `${getSliderWidth}px`;
      canvasRef.current.style.height = `${imageHeight}px`;
    }
  }, [imagehold, canvasRef, CropImageHolder, imageHN]);

  return (
    <>
      <canvas
        className={darkmodeReducer ? "turlightpostdarkx" : "turlightpostlightx"}
        ref={canvasRef}
        key={image}
        style={{
          padding: "0px",
          marginTop: index === 0 || index === 1 || index === 2 ? "0px" : "-5px",
        }}
      />
    </>
  );
}

export const PreviewCanvas = React.memo(PreviewCanvasx);
