import React, { useRef, useState, useEffect, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSpring, animated, useTransition } from "react-spring";
import Crop54Icon from "@mui/icons-material/Crop54";
import CropPortraitIcon from "@mui/icons-material/CropPortrait";
import ViewArrayIcon from "@mui/icons-material/ViewArray";
import CropIcon from "@mui/icons-material/Crop";
import ViewColumnIcon from "@mui/icons-material/ViewColumn";
import AspectRatioTwoToneIcon from "@mui/icons-material/AspectRatioTwoTone";
import StarIcon from "@mui/icons-material/Star";
import Brightness2Icon from "@mui/icons-material/Brightness2";
import AlbumIcon from "@mui/icons-material/Album";
import CheckIcon from "@mui/icons-material/Check";
import BentoIcon from "@mui/icons-material/Bento";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import CommentIcon from "@mui/icons-material/Comment";
import SentimentVerySatisfiedIcon from '@material-ui/icons/SentimentVerySatisfied';
import CircleIcon from "@mui/icons-material/Circle";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { matchMobile, matchPc, matchTablet } from "../DetectDevice";
import { UserInfoUpdateMEMBER } from "../log/actions/UserdataAction";
import AddIcon from "@mui/icons-material/Add";
import Slider from "@material-ui/core/Slider";
import {
    UpdateLoader,
    UpdateHistory,
    UpdateCommentHistory,
    UpdatePostFromCom,
    UpdateReactType, Updatepagenum
} from ".././GlobalActions";

import { useInView } from "react-intersection-observer";

function InteractMenux({
    stickerOPtionsDefault,
    setcropInitialIn,
    index,
    moveCordinatesMultiple,
    showBorder,
    setshowBorder,
    canxxtim,
    canxxTime,
    valuex,
    valuex2,
    handleChange,
    adjustinteract1,
    adjustinteract2,
    interactContent,
    setadjustinteract1,
    mobilefont,
    pcfont,
    handleChange2x

}: any) {


    ///
    ///
    ///DARKMODE FROM REDUX
    interface RootStateGlobalReducer {
        GlobalReducer: {
            darkmode: boolean;
        };
    }
    const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
        ...state.GlobalReducer,
    }));

    const darkmodeReducer = darkmode;





    return (

        <div>

            <Grid
                className={
                    darkmodeReducer
                        ? "mobileTextfield-backplateColorDark"
                        : "mobileTextfield-backplateColorLight"
                }
                item
                xs={12}
                style={{
                    width: "26%",
                    padding: "0px",
                    position: "absolute",
                    top: "0vh",
                    right: "0px",
                    height: "100vh",
                    zIndex: 20,
                    display: stickerOPtionsDefault === 4 ? "block" : "none",
                }}
            >
                <Grid
                    item
                    xs={12}
                    onClick={() => {
                        setcropInitialIn((prevArray: any) => {
                            const newArray = [...prevArray];
                            const updatedCropInitialIn = {
                                x: newArray[index].x,
                                y: newArray[index].y - moveCordinatesMultiple,
                            };
                            newArray[index] = updatedCropInitialIn;

                            if (showBorder) {
                            } else {
                                setshowBorder(true);
                            }
                            if (canxxtim.current) {
                                clearTimeout(canxxtim.current);
                            }
                            canxxtim.current = setTimeout(() => {
                                setshowBorder(false);
                            }, canxxTime);

                            return newArray;
                        });
                    }}
                    style={{
                        top: "4vh",
                        width: "50px",
                        backgroundColor: "",
                        border: "solid #ffffff",
                        height: "50px",
                        margin: "auto",
                        borderRadius: "50%",
                        position: "relative",
                        cursor: "pointer",
                    }}
                ></Grid>

                <Grid
                    item
                    xs={12}
                    onClick={() => {
                        setcropInitialIn((prevArray: any) => {
                            const newArray = [...prevArray];
                            const updatedCropInitialIn = {
                                x: newArray[index].x - moveCordinatesMultiple,
                                y: newArray[index].y,
                            };
                            newArray[index] = updatedCropInitialIn;

                            if (showBorder) {
                            } else {
                                setshowBorder(true);
                            }
                            if (canxxtim.current) {
                                clearTimeout(canxxtim.current);
                            }
                            canxxtim.current = setTimeout(() => {
                                setshowBorder(false);
                            }, canxxTime);

                            return newArray;
                        });
                    }}
                    style={{
                        top: "5vh",
                        width: "50px",
                        backgroundColor: "",
                        border: "solid #ffffff",
                        height: "50px",
                        left: "8vw",
                        borderRadius: "50%",
                        position: "relative",
                        cursor: "pointer",
                    }}
                ></Grid>

                <Grid
                    item
                    xs={12}
                    onClick={() => {
                        setcropInitialIn((prevArray: any) => {
                            const newArray = [...prevArray];
                            const updatedCropInitialIn = {
                                x: newArray[index].x + moveCordinatesMultiple,
                                y: newArray[index].y,
                            };
                            newArray[index] = updatedCropInitialIn;

                            if (showBorder) {
                            } else {
                                setshowBorder(true);
                            }
                            if (canxxtim.current) {
                                clearTimeout(canxxtim.current);
                            }
                            canxxtim.current = setTimeout(() => {
                                setshowBorder(false);
                            }, canxxTime);

                            return newArray;
                        });
                    }}
                    style={{
                        top: "-1vh",
                        width: "50px",
                        backgroundColor: "",
                        border: "solid #ffffff",
                        height: "50px",
                        left: "15vw",
                        borderRadius: "50%",
                        position: "relative",
                        cursor: "pointer",
                    }}
                ></Grid>

                <Grid
                    item
                    className={"shake"}
                    xs={12}
                    onClick={() => {
                        setcropInitialIn((prevArray: any) => {
                            const newArray = [...prevArray];
                            const updatedCropInitialIn = {
                                x: newArray[index].x,
                                y: newArray[index].y + moveCordinatesMultiple,
                            };
                            newArray[index] = updatedCropInitialIn;

                            if (showBorder) {
                            } else {
                                setshowBorder(true);
                            }
                            if (canxxtim.current) {
                                clearTimeout(canxxtim.current);
                            }
                            canxxtim.current = setTimeout(() => {
                                setshowBorder(false);
                            }, canxxTime);

                            return newArray;
                        });
                    }}
                    style={{
                        top: "-0vh",
                        width: "50px",
                        backgroundColor: "",
                        border: "solid #ffffff",
                        height: "50px",
                        borderRadius: "50%",
                        margin: "auto",
                        position: "relative",
                        cursor: "pointer",
                    }}
                ></Grid>

                <Grid
                    item
                    xs={6}
                    style={{
                        position: "relative",
                        top: "10vh",
                        margin: "auto",
                    }}
                >
                    {adjustinteract1 ? <Slider
                        value={valuex}
                        onChange={handleChange}
                        aria-labelledby="discrete-slider"
                        valueLabelDisplay="auto"
                        marks
                        min={2}
                        step={1}
                        max={30}
                    /> : null}


                    {adjustinteract2 ? <Slider
                        value={valuex2}
                        onChange={handleChange2x}
                        aria-labelledby="discrete-slider"
                        valueLabelDisplay="auto"
                        marks
                        min={2}
                        step={1}
                        max={30}
                    /> : null}
                </Grid>

                <Grid
                    item
                    xs={12}
                    style={{
                        width: "100%",
                        position: "relative",
                        top: "25vh",
                        margin: "auto",
                        textAlign: "center",
                        justifyContent: "center",
                    }}
                >
                    {adjustinteract1 && interactContent ? (
                        <img
                            src={interactContent}
                            style={{
                                width: "50%",
                                height: "auto",
                            }}
                        />
                    ) : null}

                    {adjustinteract1 && interactContent ? (
                        <img
                            src={interactContent}
                            style={{
                                width: "50%",
                                height: "auto",
                            }}
                        />
                    ) : null}

                    <CheckIcon
                        onClick={() => {
                            setadjustinteract1(false);
                        }}
                        className={
                            darkmodeReducer
                                ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                                : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
                        }
                        style={{
                            margin: "auto",
                            color: "#ffffff",
                            fontSize: matchMobile ? `${mobilefont}vh` : `${pcfont}vw`,
                            position: "absolute",
                            bottom: "-12vh",
                            textAlign: "center",
                            left: "12vw",
                        }}
                    />
                </Grid>
            </Grid>;


        </div>
    )

}

export const InteractMenu = React.memo(InteractMenux);
